css:
@-moz-document domain("modworkshop.net") {
td.thead {
    background: /*[[Color1]]*/;
}
a {
    color: white !important;
    text-shadow: 1px 0 0 #000, 0 -1px 0 #000, 0 1px 0 #000, -1px 0 0 #000;
}
[class~=forum_status] {
    background: url(http://i.imgur.com/ld3qMw6.png) no-repeat 0 0;
}
#panel, .upper {
    background: /*[[Color1]]*/ !important; 
}
#logo {
    background: /*[[Color3]]*/ !important;
}
#lbtopnav {
    border-bottom: 1px solid black !important;
    background-color: /*[[Color2]]*/ !important;
}
#shoutbox > div.thead {
    background: /*[[Color1]]*/;
    text-shadow: 1px 0 0 #000, 0 -1px 0 #000, 0 1px 0 #000, -1px 0 0 #000;
}
#shoutbox .avatar img {
    border: 2px solid /*[[Color4]]*/ !important;
}
}
