css:
@-moz-document domain("youtube.com") {
  /*[[nav-no-yes]]*/
  * {box-shadow: none !important; font-family: Open Sans, sans-serif;}
  #masthead-search {margin: 0 auto; margin-top: 3px; left: 10px;}
  #footer-main {border-bottom: 1px solid #1a1a1a;}
  .masthead-search-terms-border {border: none;}
  .branded-page-v2-secondary-column-wide .branded-page-v2-secondary-col {width: 0px !important; z-index: -1;}
  .yt-lockup-tile .yt-lockup-meta, .yt-lockup-tile .yt-lockup-description {max-width: 770px;}
  .comment-thread-renderer {padding: 5px 0;}
.watched-badge:hover {opacity: 0 !important;}
  #masthead-logo img:hover, #yt-masthead #logo-container .logo:hover, .footer-logo img:hover, #footer-logo .footer-logo-icon:hover, span.fg-eh-hj.a-b-c.fg-ph-Qc-W:hover {opacity: 1;}
  .lohp-video-metadata .yt-user-name {font-weight: bold;}
  .yt-masthead-account-picker-option .yt-masthead-picker-name {font-weight: bold;}
  .yt-masthead-picker-name {font-weight: bold;}
  #upload-btn .yt-uix-button-content {position: absolute; margin-left: -8px; margin-top: 25px;}
  .watch-card h3 {font-weight: bold;}
  #creator-sidebar .creator-sidebar-branding h1 {color: #ddd; font-weight: bold;}
  #channel-subheader li {float: none !important; display: inline-block;}
  #channel-subheader ul {float: none !important; text-align: center;}
  #c4-header-bg-container {height: 245px;}
  #c4-header-bg-container .hd-banner, .content-snap-width-3 .ie8.flex-width-enabled-snap .not-fixed-width-tab-widescreen #c4-header-bg-container .hd-banner {display: initial;}
  .primary-header-upper-section-block {width: 100%; display: block;}
  .channel-header .branded-page-header-title {display: inline-block; float: none; padding-right: 0; width: 100%; text-align: center;}
  .channel-header .primary-header-actions, .channel-header-subscription-button-container {float: none; text-align: center; width: 100%;}
  .primary-header-contents .view-as-menu {display: block; line-height: 0; float: none; position: absolute; top: 42px; right: -20px;}
  .casual-channel-settings-button-container {float: none; position: absolute; top: 12px; right: 22px;}
  .secondary-header-contents a:nth-child(3) {margin-right: 0;}
  .channel-header .about-secondary-links {margin-left: 0;}
  .channel-header .about-secondary-links .channel-links-item {padding: 0 5px;}
  .about-description pre, .channel-description pre, .about-metadata-label, .about-stats .about-stat {color: #aaa !important;}
  #channel-search {display: none;}
  .upload-sidebar-header {font-weight: bold;}
  .upload-help-link-list .help-item:hover {color: #167ac6;}
  #upload-button-text {color: #ddd;}
  .upload-item  {border: 1px solid #111;}
  .yt-alert-message {font-weight: bold;}
  #creator-subheader h2 {color: #ddd;}
  .yt-card .yt-uix-button-expander:hover {color: #888}
  .yt-card.yt-uix-expander .yt-uix-button-expander, .yt-card .yt-uix-expander .yt-uix-button-expander {margin: 10px 0 -2px;}
  .yt-uix-expander-collapsed #watch-description-text {color: #aaa !important;}
  .watch-extras-section .title {color: #aaa !important;}
  #action-panel-details a {color: #aaa !important;}
  .watch-meta-item.has-image .watch-info-tag-list li {color: #aaa !important;}
  #watch-description:hover a {color: #167ac6 !important;}
  .watch-meta-item .watch-info-tag-list li {color: #aaa !important;}
  .comment-simplebox-arrow .arrow-inner, .comment-simplebox-arrow .arrow-outer {border-bottom-color: transparent !important; border-left-color: transparent !important; position: absolute !important;}
  .comment-simplebox-arrow .arrow-inner {border: 7px solid transparent  !important;}
  .comment-simplebox-arrow .arrow-outer {border: 6px solid transparent !important;}
  #watch-header .yt-user-info a {font-weight: bold !important;}
  .creator-bar-item .yt-uix-button-icon-info, .creator-bar-item .yt-uix-button-icon-enhance, .creator-bar-item .yt-uix-button-icon-audio, .creator-bar-item .yt-uix-button-icon-annotations, .creator-bar-item
  .comment-simplebox-arrow {display: none;}
  .video-list-item:hover .title {color: #167ac6 !important;}
  #ad_creative_1 {display: none !important;}
  .content-snap-width-3 .flex-width-enabled.flex-width-enabled-snap .content-alignment {width: 1262px;}
  .content-snap-width-2 .flex-width-enabled.flex-width-enabled-snap .content-alignment {width: 1262px;}
  .multirow-shelf div ul li:nth-child(11), .multirow-shelf div ul li:nth-child(12) {display: inline-block !important;}
  .video-masthead-v2 {display: none !important;}
  .multirow-shelf .yt-uix-expander-head {padding: 6px; margin-top: 10px; font-size: 17px; text-align: center;}
  #browse-items-primary .item-section > li > .yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {border-bottom: 35px solid #eee;}
  .shelf-item .shelf-title-table .shelf-title-row .menu-container {position: absolute; top: 5px; left: 3px; z-index: 5;}
  #feed > #feed-main-what_to_watch > button {border-color: #222; width: 95%; font-size: 17px; color: #888;}
  .exp-responsive .shelf-item .branded-page-module-title .branded-page-module-title-link {z-index: 6; position: relative;}
  .filter-col-title {font-weight: bold;}
  #results .section-list li .item-section .branded-page-box {border: 1px solid #1a1a1a}
  .video-list .video-list-item .title, .related-list-item .video-time, .related-list-item .video-time-overlay, .related-list-item .video-actions:focus, .related-list-item:hover .video-actions {transition: 0.3s ease;}
  #masthead-search-terms {background: #f5f5f5;}
  .masthead-search-terms-border {border: 1px solid #f5f5f5;}
  .yt-uix-button-default:hover {background: #e2e2e2; border-color: #e2e2e2;}
  #masthead-appbar, #yt-masthead-container {border-bottom: 1px solid transparent;}
  .guide-pinned .guide-pinning-enabled #appbar-guide-menu {border-right: 1px solid transparent}
  .channel-header .secondary-header-contents {text-align: center;}
  
  

   /*[[color-r-b-g-pu-p]]*/
}
