css:
@-moz-document domain(boards.4chan.org), domain(i.4cdn.org), domain(4chan.org) {
/* ---------- Main body ---------- */
  
.yotsuba_new.nws,
body {
  background: #101010 !important;
}

div {
  color: #CCC !important;
}

a,
a.quotelink,
div#absbot a:not(:hover),
a.replylink:not(:hover) {
  color: /*[[link]]*/ !important;
}

a:hover,
a.quotelink:hover {
  color: /*[[alinkh]]*/ !important;
}
/* ---- New Thread/Post reply ---- */

input[type=text],
textarea {
  background: #202020 !important;
  border-color: #303030 !important;
  color: #CCC !important;
}

input[type=text]:focus,
textarea:focus {
  border-color: #505050 !important;
}

table.postForm>tbody>tr>td:first-child {
  background: #101010 !important;
  border-color: #660000 !important;
  color: #999 !important;
}
/* -------- Input buttons -------- */

/*[[ibutton]]*/
/* ----------- 404 box ----------- */

div.boxcontent {
  background: #202020 !important;
}

h2,
.boxbar {
  background-color: #303030 !important;
  border-color: #202020 !important;
  color: #CCC !important;
}

div.box-outer.top-box {
  color: #404040 !important;
  background: #202020 !important;
}
/* ---------- Unsorted ----------- */

.postblock {
  background-color: #303030 !important;
  border-color: #660000 !important;
}

#qrEmail input {
  background-color: #303030 !important;
}

.preview {
  border-color: #404040 !important;
}

#quote-preview,
#quote-preview .postInfo.desktop {
  background-color: #202020 !important;
  border-color: #303030 !important;
}

#boardNavDesktopFoot,
#absbot,
#boardNavDesktop {
  background: #101010 !important;
}

.post.op .postInfo.desktop {
  background-color: #101010 !important;
}

.postInfo.desktop .subject {
  color: #B30000 !important;
}

#boardNavDesktop,
.boardList,
.pages,
.prev,
.absBotDisclaimer,
.abbr {
  color: #999 !important;
}

.box-inner .boxcontent {
  background: #202020 !important;
  border-color: #404040 !important;
}

.box-inner .boxbar {
  background: #303030 !important;
  color: #CCC !important;
}

div.pagelist.desktop,
div.pages.cataloglink,
div.pages,
div.panelHeader {
  background: #202020 !important;
  border-color: #303030 !important;
}

html,
div#ft {
  background: #101010 !important;
}

p.show_content {
  color: #CCC !important;
}

.reply {
  background-color: #202020 !important;
  border-color: #303030 !important;
}

.op {
  background-color: #303030 !important;
}

.summary.desktop {
  background-color: #101010 !important;
  color: #999 !important;
}

.reply:target,
.reply.highlight {
  background-color: #303030 !important;
  border-color: #404040 !important;
}

s,
s a:not(:hover) {
  background: #101010 !important;
  color: #101010 !important;
}

s:hover,
s:focus,
s:hover a {
  color: #CCC !important;
}

.backlink a.quotelink {
  color: #339900 !important;
}

div.center,
#bannerCnt {
  border-color: #660000 !important;
}

#blotter {
  color: #660000 !important;
}

.redtxt {
  color: white !important; }

hr {
  border-color: #202020 !important;
}

div.sideArrows {
  color: #303030 !important;
}
}
