css:
@-moz-document domain("workflowy.com") {
/* Made by Malcolm Ocean http://malcolmocean.com/ */

.cols-2-proj > .children,
.cols-3-proj > .children,
.cols-4-proj > .children,
.cols-5-proj > .children,
.cols-6-proj > .children {
    overflow-y: auto;
    overflow-x: hidden;
    margin-left: 0;
}

.cols-2-proj > .children > .project,
.cols-3-proj > .children > .project,
.cols-4-proj > .children > .project,
.cols-5-proj > .children > .project,
.cols-6-proj > .children > .project {
    float: left;
    margin-left: 1px !important;
}

.cols-2-proj > .children > .project {
    width: 49.5%;
}
.cols-3-proj > .children > .project {
    width: 32.8%;
}
.cols-4-proj > .children > .project {
    width: 24.5%;
}
.cols-5-proj > .children > .project {
    width: 19.6%;
}
.cols-6-proj > .children > .project {
    width: 16.2%;
}
}
