css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gravitytales.com") {
  body {
    background-image: url("/*[[background]]*/") fixed center no-repeat !important;
  }  
  .navbar, footer {
    background: rgba(30, 30, 30, 0.5) !important;
  }
  
  .header a:hover, .header a, .navbar a, .navbar.nav a, h3 {
    color: #ccc !important;
  }
  
  div.container.wrap {
    background: rgba(0, 0, 0, 0.95) !important;
    color: #999 !important;
  }
  div.entry-content span[style~="#ffffff"] {
    color: transparent !important;
  }
  
  .side-nav {
    background: transparent  !important;
  }
  
  .navbar-static-top .dropdown-menu > li > a {
    border-top: none !important;
  }

  .panel {
    background: none !important;
  }
  
  .panel-heading {
    background: rgba(30,  30, 30, 1) !important;
  }
  .latest-chapter .chapter.row {
    background: rgba(30, 30, 30, 0.5) !important;
    border: none !important;
  }
  em {
    font-style: normal !important;
  }
  
  ul.pagination, .pagination {
    width: 100% !important;
    margin-left: 10% !important;
  }
  
  .widget {
    background: rgba(30, 30, 30, 0.5) !important;
  }
  
  .widget-title, .widget-title span {
    background: none !important;
    color: #ccc !important;
    padding-left: 0px !important;
  }
  
  .nav.nav-tabs .ng-scope.active a.ng-binding:first-child, .nav.nav-tabs > li.active > a, .nav.nav-tabs > li.active > a:hover, .nav.nav-tabs > li.active > a:focus, .nav.nav-tabs > li > a:hover {
    background: rgba(15, 15, 15, 0.5) !important;
    color: #aaa !important;
    border: 1px solid #ccc !important;
  }
  
   .tab-content {
    background: rgba(15, 15, 15, 0.5) !important;
  } 
  
  .comments-widget .comment-img {
    background: rgba(30, 30, 30, 1) !important;
  }
  
  .comment.ng-scope:hover, .comment.ng-scope {
    background: rgba(30, 30, 30, 0.5) !important;
  }
  
  #commentsList {
    background: transparent !important;
  }
  
  textarea {
    background: rgba(30, 30, 30, 1 ) !important;
    color: #999 !important;
  }
    
  footer div.container {
    color: #999 !important;
  }
}
