@-moz-document domain("tss.asenheim.org") {
#sfx_player,
#voice_player,
#music_player {
    -webkit-transition-duration: 0.3s;
    -moz-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
    transition-duration: 0.3s;
    display: block;
    opacity: 0.15;
    filter: blur(16px) grayscale(100%);
    transform-origin: top left;
    transform: scale(0.25, 0.5);
}
#sfx_player:hover,
#voice_player:hover,
#music_player:hover {
    opacity: 0.7;
    filter: blur(0px) grayscale(0%);
    transform: scaleX(1);
}
#options_button {
    background-image: url('https://32bit.gitlab.io/asenheim/options.png');
}
#save_button {
    background-image: url('https://32bit.gitlab.io/asenheim/save.png');
}
#load_button {
    background-image: url('https://32bit.gitlab.io/asenheim/load.png');
}
#log_button {
    background-image: url('https://32bit.gitlab.io/asenheim/log.png');
}
#screenshot_button {
    background-image: url('https://32bit.gitlab.io/asenheim/screenshot.png');
}
#exit_button {
    background-image: url('https://32bit.gitlab.io/asenheim/exit.png');
}
#report_button {
    background-image: url('https://32bit.gitlab.io/asenheim/report.png');
}
#help_button {
    background-image: url('https://32bit.gitlab.io/asenheim/help.png');
}
#options_button,
#save_button,
#load_button,
#log_button,
#screenshot_button,
#exit_button,
#report_button,
#help_button,
#ajax_button {
    width: 32px;
    height: 32px;
    margin: 4px;
    background-size: 32px 32px;
    background-repeat: no-repeat;
    -webkit-transition-duration: 0.3s;
    -moz-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
    transition-duration: 0.3s;
}
#options_button:hover,
#save_button:hover,
#load_button:hover,
#log_button:hover,
#screenshot_button:hover,
#exit_button:hover,
#report_button:hover,
#help_button:hover,
#ajax_button:hover {
    transform: scale(2);
    filter: drop-shadow(0px 6px 1px rgba(0, 0, 0, 0.8));
}
#ajax_indicator {
    width: 32px;
}
#user_panel {
    -webkit-transition-duration: 0.3s;
    -moz-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
    transition-duration: 0.3s;
    opacity: 0.2;
    filter: blur(4px);
    width: auto;
    height: auto;
    image-rendering: auto;
    image-rendering: auto;
    image-rendering: auto;
    image-rendering: auto;
    image-rendering: auto;
    transform-origin: top right;
    -ms-interpolation-mode: auto;
}
#user_panel:hover {
    opacity: 0.7;
    filter: blur(0px) grayscale(0%);
    padding-top: 4px;
    padding-right: 8px;
}
body.static {
    filter: contrast(100%);
    background-color: #000;
    image-rendering: optimizeSpeed;
    image-rendering: -moz-crisp-edges;
    image-rendering: -webkit-optimize-contrast;
    image-rendering: -o-crisp-edges;
    image-rendering: pixelated;
    -ms-interpolation-mode: nearest-neighbor;
}
#game_container {
    /* transform: scale(2);
    -moz-transform: scale(2);*/
}
#game_container::after {
    pointer-events: none;
    background-image: url('https://32bit.gitlab.io/asenheim/scanlines.gif');
    position: fixed;
    top: 0;
    left: 0;
    z-index: 100;
    width: 100%;
    height: 100%;
    content: " ";
    opacity: 0.2;
}
#right_click_handle {
    -webkit-transition-duration: 0.3s;
    -moz-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
    transition-duration: 0.3s;
    opacity: 0.33;
}
#right_click_handle:hover {
    opacity: 1;
}
.center {}
canvas[id^="c_"] {}



