css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* left padding */
#TabsToolbar #tabbrowser-tabs .tabbrowser-arrowscrollbox .arrowscrollbox-scrollbox 
{
  padding-left : 0 !important;
}

/* tab's height */
#TabsToolbar,
#TabsToolbar #tabbrowser-tabs,
#TabsToolbar tab,
#TabsToolbar tab .tab-stack,
#TabsToolbar tab .tab-stack .tab-background,
#TabsToolbar tab .tab-stack .tab-content,
#TabsToolbar tab .tab-stack .tab-background .tab-background-middle
{
  min-height : /*[[tabHeight]]*/ !important;
  max-height : /*[[tabHeight]]*/ !important;
  height : /*[[tabHeight]]*/ !important;
}

/* hide tab's left and right decoration */
#TabsToolbar .tabbrowser-tabs tab .tab-background .tab-background-start,
#TabsToolbar .tabbrowser-tabs tab .tab-background .tab-background-end
{
  display: none !important;
}

/* hide tab's decoration */
#TabsToolbar tab .tab-stack .tab-background,
#TabsToolbar tab .tab-stack .tab-background .tab-background-middle,
#TabsToolbar tab .tab-stack .tab-content,
/* hide icon's decoration */
#TabsToolbar tab .tab-stack .tab-content .tab-icon-image,
#TabsToolbar tab .tab-stack .tab-content .tab-throbber,
#TabsToolbar tab .tab-stack .tab-content .tab-icon-overlay,
#TabsToolbar tab .tab-stack .tab-content .tab-close-button.close-icon,
#TabsToolbar tab .tab-stack .tab-content .tab-icon-sound
{
  margin : 0 !important;
  padding : 0 !important;
  border : none !important;
}

/* inactive tab background */
#TabsToolbar tab .tab-stack:hover .tab-background
{
  background : none !important;
}

#TabsToolbar .tabs-newtab-button
{
  padding : 0 !important;
  margin : 0 !important;
  border: 0 !important;
}

/* border for pretty */
#TabsToolbar tab .tab-stack .tab-content
{
  border-top : /*[[borderHeight]]*/ solid !important;
  border-bottom : /*[[borderHeight]]*/ solid !important;
  border-left : /*[[borderHeight]]*/ solid !important;
  border-right :  /*[[borderHeight]]*/ solid !important;
  border-color : /*[[borderColor]]*/ !important;
  border-style : /*[[borderStyle]]*/ !important;
  
  /* need for fixing border bottom position */
  margin-bottom: 1px !important;
}

/* tab background color */
#TabsToolbar tab
{
  background-color: /*[[tabColor]]*/ !important;
}

/* selected tab background color */
#TabsToolbar tab[selected="true"] .tab-background-middle
{
  background-color: /*[[selectedTabColor]]*/;
}

#TabsToolbar
{
  background-color: /*[[tabsToolbarColor]]*/ !important;
}

/* border or not */
#TabsToolbar tab .tab-stack .tab-content
{
  /*[[topTabBorder]]*/
  /*[[bottomTabBorder]]*/
  /*[[leftTabBorder]]*/
  /*[[rightTabBorder]]*/
}

/* background or not */
#TabsToolbar tab
{
  /*[[tabBackgd]]*/
}

/* selected tab background color */
#TabsToolbar tab[selected="true"] .tab-background-middle
{
  /*[[selectedTabBackgd]]*/
}

#TabsToolbar
{
  /*[[tabsToolbarBackgd]]*/
}

/* fix bad visibility of tab icon background */
#TabsToolbar tab:not([selected="true"]) > .tab-stack > .tab-content .tab-throbber,
#TabsToolbar tab:not([selected="true"]) > .tab-stack > .tab-content .tab-icon-image,
#TabsToolbar tab:not([selected="true"]) > .tab-stack > .tab-content .tab-icon-overlay
{
  background-color: /*[[tabIconBckrgClr]]*/ !important;
  /*[[tabIconBckrg]]*/
}
