css:
/* i really want this to be global */
@-moz-document domain("backlog.jp") {
/**
  * gnav
  */

.global-header {
    height: 40px;
}
.core-wrapper {
    top: 40px;
}
#projectNav {
    top: 40px;
    height: calc(100% - 40px);
}
.global-nav__link,
.global-nav__link.space-icon-set .space-icon-set__text {
    padding: 4px 7px;
}
.global-header__user-actions {
    padding-top: 0;
    padding-bottom: 0;
}
.project-nav__back {
    background-color: #347944;
}
.project-nav-list__link {
    padding-top: 8px;
    padding-bottom: 8px;
}
.button-group__link.is_active {
    background-color: #50956d;
}
/* task list */

.data-table th,
.data-table td {
    padding: 6px 8px 5px;
}
.data-table th.sort {
    text-align: left;
}
.sort__icon {
    padding-right: 12px;
    padding-left: 4px;
}
.data-table .pill {
    font-size: 1rem;
}
.cell-user,
.cell-status {
    font-size: 1rem;
}
.cell-user .user-icon-set__user-icon {
    padding-right: 4px;
}
.cell-user .image.-small {
    width: 16px;
    height: 16px;
}
/* new task */

input#summaryInput {
    font-size: 2rem;
    padding-top: 4px;
    padding-bottom: 4px;
}
.button--primary {
    background-color: #3e7455;
}
.ticket__properties-item {
    border-bottom-color: #e5e5e5;
}
.ticket__properties-item:nth-child(-n+2) {
    border-top-color: #e5e5e5;
}
.ticket__properties-label {
    justify-content: flex-end;
    padding: 5px 10px 4px 5px;
}
.ticket__properties-value {
    padding: 5px 0 4px 5px;
}
form .ticket__properties-label {
    align-items: baseline;
    line-height: 3.2rem;
}
form .ticket__properties-value {
    align-items: baseline;
}
form .ticket__properties-item.-status .ticket__properties-value {
    line-height: 3.2rem;
}
.ticket__content {
    padding-top: 10px;
    padding-left: 15px;
    padding-right: 15px;
}
.content-main>#commentArea {
    margin-top: -15px;
}
/* card */

.card--default {
    border-color: #666;
    border-width: 2px;
}
.tab-button-content,
.tab-buttons__link::after {
    border-color: #666;
}
/* comment bar */

.core-wrapper.-project .comment-editor.-fixed-footer {
    width: calc(100% - 200px);
}
.core-wrapper.-project.is_project-nav-collapsed .comment-editor.-fixed-footer {
    width: calc(100% - 50px);
}
.core-wrapper.-project .comment-editor.-fixed-footer.is_comment-all-collapsed {
    background-color: #fafafa;
    width: calc(50% - 50px);
}
/* ticket date */

.ticket__date {
    position: absolute;
    left: 230px;
    top: 106px;
}
}
