css:
@-moz-document url-prefix('https://vk.com') {

#ads_left
{
    display: none !important;
}
#groups_filters_wrap,
.left_menu_nav_wrap,
.counts_module,
#submit_post_box a[class$='user_image'],
.left_settings,
.post_field_user_image,
.sticker_hints_tt,
#main_feed > .wall_module > #submit_post_box,
#feed_filters > .page_block > .right_list_header,
#feed_filters > .page_block > #group_recom_wrap,
#privacy_friends_hide
{
    display: none !important;
}

.left_icon
{
    visibility: hidden !important;

    margin-right: -24px !important;
}
h2.page_block_h2
{
    margin-top: -15px;
}
#photos_albums_block
> .page_block_h2
{
  margin-top: -2px;
}
#submit_post_box.shown a
{
    display: block;
}
#side_bar_inner,
.narrow_column.fixed
{
    position: static !important;
}
.page_info_wrap,
.photos_container_album_grid
{
    padding-bottom: 0;
}
#profile .narrow_column.fixed
{
    position: relative;
}
body
{
    background-color: white !important;
    font-family: 'Tahoma' !important;
}
.page_block
{
    box-shadow: none !important;
}
.reply_field_wrap
{ 
    margin: 8px 0px !important;
    border-radius: 6px;
}
.module_header
{
    overflow: visible;

    padding-bottom: 3px;

    border-radius: 3px;
    background-color: #e5ebf1;
}
.module_header
.header_top
{
    line-height: 36px;
}
.wide_column .module_header .header_top
{
    padding-top: 0 !important;
}
._audio_layout .page_block
{
    margin-top: 6px;
}
.wide_column_right > .narrow_column_wrap > .narrow_column
{
    margin-top: 0; /* left panel fixing end-of-scroll bugfix */
}

.page_top,
.module,
.post_full_like_wrap,
.submit_post,
.wall_module .reply_box,
.wall_module .reply_fakebox_wrap,
.wall_module .reply ~ .reply .dld,
.wall_module .reply ~ .reply .reply_wrap,
.im-page.im-page_classic .im-page--header,
.nim-dialog .nim-dialog--content,
#ui_albums_load_more,
#photos_albums_block .clear_fix,
#side_bar_inner > ol > .more_div
{
    border: none !important;
}
.im-page.im-page_classic .im-page--header
{
    position: absolute;
    top: 65px !important;

    border-top: solid 15px white !important;
}
#profile_photos_module > .header_right_link
{
    margin-top: -6px !important;
    margin-right: 14px !important;
}
.im-page.im-page_classic .im-page--dialogs-search
{
    overflow: hidden;

    border-radius: 2px;
}
.like_cont,
.wall_module .replies_list,
.im-page.im-page_classic .im-page--header-chat,
.deep_active .replies .replies_wrap_deep,
.deep_active.wall_module .replies .replies_wrap_deep, 
.deep_active.wall_module .wl_replies_block_wrap .replies_wrap_deep, 
.deep_active .wl_replies_block_wrap .replies_wrap_deep
{
    border-top: none;
}
.im-page.im-page_classic .im-page--chat-input
{
    border-right: none;
    border-bottom: none;
}
#side_bar .more_div {
    margin-left: 0;
}
.deep_active .replies .reply_send_button, 
.deep_active.wall_module .replies .reply_send_button, 
.deep_active.wall_module .wl_replies_block_wrap .reply_send_button, 
.deep_active .wl_replies_block_wrap .reply_send_button {
    right: -40px;
}
.deep_active .replies .reply_fakebox_container, 
.deep_active.wall_module .replies .reply_fakebox_container, 
.deep_active.wall_module .wl_replies_block_wrap .reply_fakebox_container, 
.deep_active .wl_replies_block_wrap .reply_fakebox_container {
    margin: 0;
}
.deep_active .replies .replies_wrap_deep .replies_short_text_deep,
.deep_active.wall_module .replies .replies_wrap_deep .replies_short_text_deep, 
.deep_active.wall_module .wl_replies_block_wrap .replies_wrap_deep .replies_short_text_deep,
.deep_active .wl_replies_block_wrap .replies_wrap_deep .replies_short_text_deep {
    padding-left: 0;
}
.reply_fakebox_wrap > .reply_send_button,
.reply_fakebox_container > button,
.reply_fakebox_container > span {
    display: none;
}
.audios_module .module_body
{
    padding: 12px 4px;
    padding-top: 8px;
}
.deep_active .replies .reply_fakebox_wrap,
.deep_active.wall_module .replies .reply_fakebox_wrap,
.deep_active.wall_module .wl_replies_block_wrap .reply_fakebox_wrap,
.deep_active .wl_replies_block_wrap .reply_fakebox_wrap {
    text-align: left;
    top: 0px;
    margin-left: 7px;
    margin-right: 0px;
}
.reply_fakebox_wrap,
.reply_fakebox_container,
.deep_active .replies .reply_fakebox,
.deep_active.wall_module .replies .reply_fakebox,
.deep_active.wall_module .wl_replies_block_wrap .reply_fakebox,
.deep_active .wl_replies_block_wrap .reply_fakebox {
    border-color: transparent;
    background: transparent !important;
}
.page_photo
{
    padding-top: 0;
}
#photos_albums_block
{
    margin-top: 2px;
}
#page_info_wrap
{
    padding-top: 4px;
}
.post_fixed
{
    margin-top: 20px !important;
}
.nim-dialog
{
    padding-left: 6px !important;
}
.im-page.im-page_classic .im-page--dialogs-footer
{
    height: 36px !important;
    padding: 12px 20px;
}
._im_dialogs_settings
{
    opacity: 0;
}
._im_dialogs_settings:hover
{
    opacity: .9;
}
#friends_possible_block > *
{
    display: none;
}
#ui_albums_load_more
{
    margin: 0 20px;
    margin-top: 6px;
    padding: 11px 0 10px;
}
.people_module > .header_right_link
{
    margin-top: -2px;
}
#submit_post_box
{
    margin: -10px 0;

    border: solid 1px #e7e8ec !important;
}
#submit_post_box .ph_input
{
    margin-left: -36px;
}
#submit_post_box.shown,
#submit_post_box.shown .ph_input
{
    margin: 0;
}
#submit_post_box.shown > .post_field_wrap._emoji_field_wrap
{
    margin-left: 0;
}
#groups_create_btn
{
    padding-top: 8px;

    opacity: .5;
    color: #2a5885;
    background-color: white;
}
#groups_create_btn:hover
{
    text-decoration: underline;

    opacity: 1;
}
.body_im #side_bar
{
    position: fixed;
    top: 0;
}
.im-page--header.ui_search._im_dialogs_search
{
    border-top-width: 11px !important;
}
/*[[square_ava]]*/
.page_list_module .line_cell .desc_info,
.page_list_module .line_cell .extra_info,
.page_list_module .line_cell .info
{
    padding-top: 0 !important;
}
.emoji_smile_cont
{
    display: inline-block !important;
}
.im-page_classic .im-page--chat-header
{
    border-top: none !important;
}

