css:
@-moz-document url-prefix("https://forum.lowyat.net"), url-prefix("https://forum.lowyat.net") {
/*[[font]]*/
/*[[submenu]]*/

body{background:url(/*[[background]]*/);
background-repeat:/*[[bgr]]*/;
background-attachment:fixed;
background-position:center;
background-size:auto;
background-color:/*[[bgc]]*/

}
}

@-moz-document url-prefix("http://forum.lowyat.net"), url-prefix("https://forum.lowyat.net") {
.quotemain,
.quotetop,
SPAN.member-tagged,
.darkrow3,
#replies_to_me LI,
#starred_tabs LI A.selected, #starred_tabs LI A:hover,
#submenu,#userlinks, #userlinksguest,
.newslink,
.borderwrap,
.minipagelink, .minipagelinklast,.pagelink,.pagelinklast,
.gsc-results-wrapper-overlay,
#ipbcontent,.ipbpagespan,.ipbnav,.ipbnavsmall,
.toplinks span,
.textarea,
textarea,
#ucpmenu,
#ucpcontent
{
background:rgba(64,64,92,.7)!important}

.borderwrap h3, .maintitle, .maintitlecollapse,
.row2,.row1,th,.catend,.formsubtitle,#gfooter,
.newslink input,
#replies_to_me,
.post2,
TD.post_td_left,TD.post_td_right,
.bar, .barb, .barc,
.subtitle,
.borderwrap p,
input,
.darkrow1,
.globalmessage,
.post1, .bg1,
td.formbuttonrow,
.activeusers,
.spoilermain,
div#lb_top,
.formtable td, .pformleft, .pformleftw, .pformright,
.subtitlediv,
.cse .gsc-control-cse, .gsc-control-cse,
.gsc-modal-background-image,
.codemain,
DIV.postcolor TABLE,
.gsc-results, .gsc-cursor-box, .gsc-cursor-page,
.borderwrapm,
.tablepad,td
{
background:none!important
}

body,a,b,.newslink span,td,
table th, .borderwrap table th, .subtitle, .subtitlediv, .postlinksbar,
.forumdesc,#replies_to_me LI,
.quotemain,
.quotetop,
.desc,
.lastaction,
.formsubtitle,
.button,
textarea,
select,
.newslink input,
.textarea,.helpbox,.codebuttons,
input,.spoilermain,.codemain,.signature span,.signature div,
.gs-webResult .gs-snippet, .gs-imageResult .gs-snippet, .gs-fileFormatType,
.gsc-results, .gsc-cursor-box, .gsc-cursor-page,.signature,
#navstrip
{
color:/*[[fc1]]*/!important;
text-shadow: 1px 1px 4px #000000
}

.borderwrap,#userlinks,.newslink,
.minipagelink, .minipagelinklast,.pagelink,.pagelinklast,.pagecurrent,
img.avatar,
.quotemain,DIV.post_text IMG,
.globalmessage
{
box-shadow: 0px 5px 15px /*[[boxshadow]]*/
}

.borderwrap, .borderwrapm,#submenu,
#userlinks, #userlinksguest,
.newslink,
.maintitle,
table th, .borderwrap table th,
input.gsc-input,
.minipagelink, .minipagelinklast,
.pagelink, .pagelinklast, .pagecurrent, .minipagelink, .minipagelinklast,
.darkrow1,.newslink input,
.borderwrap,#ucpmenu, #ucpcontent,
textarea,.button,.codebuttons,.searchinput,.button
{
border: 1px solid /*[[bordercolor]]*/ !important
}

.pagecurrent{
background:rgba(192,192,255,.3);
color:#FFFFFF}
#topic_replies A{color:black!important;
}
#topic_replies
{
color:black!important;
background:rgba(177, 220, 255, 0.7)!important
}

select,.gsc-webResult.gsc-result, .gsc-imageResult,.gsc-webResult.gsc-result:hover, .gsc-imageResult:hover
{
background:#212121}

.globalmessage H1,
#starred_tabs LI A
{background-color:rgba(170,17,17,0.5)!important}

.globalmessage P{
background-color:rgba(213,153,153,.5)!important}

.errorwrap p, #pmnotewrap p
{
background:rgba(153,42,42,.3)!important
}
.errorwrap h4, #pmnotewrap h4,
.errorwrap p, #pmnotewrap p
{
color: /*[[fc1]]*/ !important;
text-shadow: 1px 1px 4px #000000
}
td.formbuttonrow.post_td_left,td.formbuttonrow.post_td_right,TD.post2.post_td_left
.subtitle
{
border-bottom: 1px solid /*[[bordercolor]]*/ !important
}
.post2 a:link:hover,
.post2 a:active:hover,
.post2 a:visited:hover
{
text-decoration:underline;
color:pink!important
}

.gsc-control-cse,#ads_bottom,#ads_bottom_leaderboard2,.barc,.subtitlediv,.subtitle
{
border:none
}
td.formbuttonrow
{
border-top:none
}
#ads_bottom,#ads_bottom_leaderboard2
{
display:none!important
}
td a span
{
color:cyan!important;
text-shadow: 1px 1px 4px #000000
}
}

@-moz-document url-prefix("https://forum.lowyat.net/index.php?act=Attach&type=post&id=") {
body
{
background:black!important
}
}
