css:
/* -=< Darker userstyles.org by Zigboom Designs >=- */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
html, body, #nav-view-responsive, .overlay_background, .navigation {
    color:#aaaaaa !important;
    background:#181818 !important;
}

 * {
    border-color:#444444 !important;
     color: #aaa !important;
}

/* Hide Some Ads */

 #banner-nologo, .adsbygoogle {
    display:none !important;
}             
        
 #main-header {
    background:none !important;
}

 a {
    color:#cccccc !important;
    text-decoration:none !important;
    text-shadow:none;
    transition:all .6s;
}

 a:hover, h4 a:hover, a:visited:hover, h4 a:visited:hover {
    color:#eeeeee !important;
    text-shadow:0 0 6px #ffffff !important;
    transition:all .6s;
}
 
a:visited, h4 a:visited {
    color: #777 !important;
}
        
 #left-sidebar > * {
    background:none !important;
}

.navigation .links-box .navigation-link, .navigation .links-box {
        box-shadow: 0 -1px 0 0 #444, 0 -2px 0 0 #000, 0 -2px 0 0 #000, 0 -2px 0 0 #000;
}

.navigation .links-box .navigation-link:hover, .us-stylecard--short, .rh, .us-stylecard--long {
    background-color: #333 !important;
}

#top_android_button, .header-search .type_search, .personal-page .light-blue-button, .us-stylecard__button-container .us-button, .current, .div-arrow, #create_new_style { 
background: #444 !important;
}

#top_android_button:hover, .header-search .type_search:hover, .personal-page .light-blue-button:hover.us-stylecard__button-container .us-button:hover, .current:hover, .div-arrow:hover, .android_button_banner .android_button_banner_right .android_button_banner_cta, #create_new_style:hover { 
background: #555 !important;
}

 #install {
    background:none !important;
    color:#aaaaaa !important;
}

 .edit-style-option:nth-child(2n+1) {
    background:none !important;
}

#overlay-info {
    background-color: rgba(0, 0, 0, 0.98) !important;
}

 #vanilla_discussions_index, pre, a.BigButton, #Head, .MessageForm, .Tabs, .Headings, .TabLink, .TabLink:link, 
 .TabLink:hover, .TabLink:active, .TabLink:visited, .Active .Tablink, .DataList .Meta a.Category, .Box,
  .PanelInfo li, #vanilla_post_discussion, #DiscussionForm form,  .Activities .DataList li, .Foot, .Tabs .SubTab,
   code, pre, .MessageList .Item, body.Discussion #Content, .DiscussionsTabs, .DataList .Item, #nav-view, #navigation  {
    background:none !important;
}
        
         #header, .style_card, .see_more, .white_button, #css_button, #notes_textarea, #send_feedback_button, #archive_style_button, #update_style_button, #style_installed_button, #install_style_button, #footer, .style_category_card_wrapper, .NotificationLine {
            background: #282828 !important;
        }
        
        .navigation_link:hover, #search_input, .see_more:hover, #search_input input, .white_button:hover, 
        #css_button:hover, #send_feedback_button:hover, #archive_style_button:hover, #update_style_button:hover, #style_installed_button:hover, #install_style_button:hover, .fzKkUK {
            background: #333 !important;
        }
        


#install_style_button {
    box-shadow: none !important;
    border: 1px solid #444 !important;
    border-radius: 4px !important;
}

        .see_more, #search_input {
            border: 1px solid;
        }

 .Box dl dt, span.Title, h1, h2, h3, h4, h5, h6, .navigation_title, #copyrights, #bottomLinks  {
    color:#aaaaaa !important;
}

 #DiscussionForm label, .Activities li.Condensed .Excerpt, #create_new_style a, .footer_link a, .overrideHref a, h4 a, .author-name a, #as_userscript a, #install_style_button div, .android_button_banner_cta div {
    color:#eeeeee !important;
    text-decoration: none !important;
}
        
        #as_userscript a:hover {
            color: #fff !important;
             text-decoration: none !important;
        }

 #vanilla_discussion_index, #dashboard_activity_index, #conversations_messages_all, #dashboard_profile_index,
  #vanilla_categories_index {
    background:none !important;
    color:#eeeeee !important;
}

 .Attachment:hover .FileHover {
    background:#000000 !important;
}

 #post-install-ad {
    display:none !important;
}

 INPUT:not([type="file"]), TEXTAREA {
 -moz-appearance: none !important; 
 background-color: #282828 !important; 
 color: #aaaaaa !important; 
 border: 1px solid #444444 !important; 
 }
 
 #search-submit, INPUT[type="file"] {
 -moz-appearance: none !important; 
 background: linear-gradient(#333333, #181818) !important; 
 color: #aaaaaa !important; 
 border: 1px solid #444444 !important; 
 border-top-right-radius: 9px !important; 
 border-bottom-right-radius: 9px !important;  
 }
 
  #search-submit:hover, INPUT[type="file"]:hover {
 background: linear-gradient(#444444, #222222) !important;  
 }
 
 #search-submit {
  float: left !important; 
  }
 
 #search-terms {
 -moz-appearance: none !important; 
 background: linear-gradient(#181818, #333333) !important; 
 color: #aaaaaa !important; 
 border-right: 0px solid #181818 !important; 
 border-top-left-radius: 9px !important; 
 border-bottom-left-radius: 9px !important; 
 padding-left: 4px !important; 
 width: 95px !important; 
 -moz-transition: all 1s !important; 
 margin-bottom: 12px !important; 
 }
 
 #search-terms:focus {
  width: 125px !important; 
  -moz-transition: all 1s !important; 
  }
        
.statistic table, th, td, #search_input input, .MeMenu .MeButton {
    border: 0px !important;
}

#create_new_style {
    min-height: 42px !important;
    margin-left: -3px !important;
}


.author-styles {
  border: 1px solid #333 !important;
    border-collapse: collapse;
}        
        
.author-styles td {
  border-top: 1px solid #444 !important;
  padding: 4px 5px !important;
}

.author-styles th {
  padding: 4px 5px 4px !important;
}
        
.numeric-value {
    text-align: center !important;
}

.update-icon, #install_style_icon, .navigation .links-box .navigation-link .logo-navigation-link, .init_loader, .MeMenu .MeButton {
    filter: grayscale(1) invert(0.9);
}

.update-icon {
    mix-blend-mode: lighten;
}
        
.logo, .installed-icon, .android_button_button, #social_networks, .android_button_banner, .robot_android, .android_walking, #donate input {
    filter: grayscale(1);
    transition: all 0.6s;
}

.android_button_button .relative_div .x_button {
    filter: grayscale(1) invert(0.8);
    border: 1px solid #aaa !important;
}
   
.logo:hover {
    filter: grayscale(0);
}
        
  
/* --- */
}
