css:
@-moz-document domain("osustats.ppy.sh") {
/*==CUSTOMIZABLE ON USERSTYLES.ORG==*/
body{
background-image: url("/*[[CUSTOMIMAGE]]*/") !important;
background-size: cover !important;
background-position: 50% !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important
}
/*[[LOGO]]*/
/*==CODE==*/
*{
color: #FFF !important;
text-shadow: 0 0 10px #FFF !important;
font-family: Exo, Tahoma, Arial, sans-serif !important;
}
.opt tr td{
border: 1px solid #FFF !important;
border-radius: 6px !important;
background: rgba(0,0,0,0.4) !important;
}
.opt tr:nth-child(8) td:first-child{
border: none !important;
background: transparent !important;
}
.dd .ddTitle{
background: transparent !important;
}
.dd .ddTitle .ddTitleText:after{
content: " â–¼" !important;
}
.dd .ddTitle .ddTitleText{
cursor: pointer !important;
}
.optinput{
background: rgba(0,0,0,0.6) !important;
border: 1px solid rgba(255,255,255,0.6) !important;
}
.dd .ddChild{
background: transparent !important;
}
.dd .ddChild li{
background: rgba(0,0,0,0.9) !important;
border-bottom: none !important;
}
.dd .ddChild li:hover{
background: rgba(0,176,208,0.9) !important;
border-bottom: none !important;
cursor: pointer !important;
}
.resultbox{
background: rgba(0,0,0,0.75) !important; 
}
.notinfourty{
border: 3px solid #FFFFFF !important
}
.firstplace{
box-shadow: rgb(218,175,42) 0px 0px 6px !important;
border: 3px solid rgb(218,175,42) !important;
}
.doinfourty{
border: 3px solid rgba(0,176,208,1) !important; 
box-shadow: rgba(0,176,208,1) 0px 0px 6px !important;
}
#language, #changelog,.username{
width:250px !important;
background: rgba(0,0,0,0.6) !important;
border: 1px solid #FFF !important;
}
#click, .resbutton{
background: rgba(0,0,0,0.6) !important;
border: 1px solid #FFF !important;
}
#changelog{
padding-bottom:0px !important;
height: 46px !important;
}
.resultTable{
padding-top: 20px !important;
}
.resultTable *{
text-shadow: none !important;
}
.resultTable thead td{
background: rgba(0,0,0,0.7) !important;
}
.resultTable thead tr td:first-child{
border-left: 1px solid #FFF !important;
}
.resultTable thead tr td:first-child,.resultTable thead tr td:nth-child(2),.resultTable thead tr td:nth-child(3),.resultTable thead tr td:nth-child(4),.resultTable thead tr td:nth-child(5),.resultTable thead tr td:nth-child(6),.resultTable thead tr td:nth-child(7),.resultTable thead tr td:last-child{
border-top: 1px solid #FFF !important;
border-bottom: 1px solid #FFF !important;
}
.resultTable thead tr td:last-child{
border-right: 1px solid #FFF !important;
}
.pr.r1{
background: rgba(0,0,0,0.5) !important;
}
.pr.r2{
background: rgba(0,0,0,0.7) !important;
}
.resultTable tbody tr td:first-child{
border-left: 1px solid !important;
padding-top: 3px !important;
padding-bottom: 3px !important;
}
.resultTable tbody tr td:last-child{
border-right: 1px solid #FFF !important;
}
.resultTable tbody tr:last-child td{
border-bottom: 1px solid #FFF !important;
}
#preview{
border: 3px solid #FFF !important;
box-shadow: none !important;
background-color: rgba(0,0,0,0.8)
}}
