css:
@-moz-document url-prefix("https://www.youtube.com/tv"), url-prefix("http://www.youtube.com/tv"), url-prefix("https://youtube.com/tv"), url-prefix("http://youtube.com/tv") {
.desktop .legend-item.next .icon {
    background-position: 0 20.7%;
    background-size: 240%;
}

#legend.back .modify,
#legend.search .modify {
    display: inline-block;
}

#legend.back .modify > .title,
#legend.search .modify > .title {
    visibility: hidden;
    width: 46px;
}

#legend.back .modify > .icon,
#legend.search .modify > .icon {
    background-position: 77% 47.4%;
    background-size: 240%;
}

#legend.back .modify > .title:before,
#legend.search .modify > .title:before {
    content: "Select";
    visibility: visible;
}

.desktop .legend-item .icon {
    background-image: /*[[buttons]]*/;
}
}

@-moz-document url-prefix("https://www.youtube.com/tv#/watch/video"), url-prefix("http://www.youtube.com/tv#/watch/video"), url-prefix("https://youtube.com/tv#/watch/video"), url-prefix("http://youtube.com/tv#/watch/video") {
#legend.back > .delete.legend-item,
#legend.search > .delete.legend-item {
    display: inline-block;
}

#legend.back > .delete.legend-item > .title,
#legend.search > .delete.legend-item > .title {
    visibility: hidden;
    width: 78px;
}

#legend.back > .delete.legend-item > .icon,
#legend.search > .delete.legend-item > .icon {
    background-position: 77% 73.7%;
    background-size: 240%;
}

#legend.back > .delete.legend-item > .title:before,
#legend.search > .delete.legend-item > .title:before {
    content: "Play/Pause";
    visibility: visible;
}
}
