css:
@-moz-document domain("twitter.com") {
  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir.eot?#iefix') format('embedded-opentype'),
        url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir.woff') format('woff'),
        url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir.ttf') format('truetype');
    font-weight: normal;
  }

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Bold.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
        url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Bold.woff') format('woff'),
        url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Bold.ttf') format('truetype');
    font-weight: bold;
  }

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Light.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
        url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Light.woff') format('woff'),
        url('https://cdn.rawgit.com/rastikerdar/vazir-font/v21.1.0/dist/Vazir-Light.ttf') format('truetype');
    font-weight: 300;
  }
  body{
      font-family: 'vazir';
  }
  .ProfileAvatar-image, .ProfileAvatar-placeholderImage, .avatar, .nav .session .dropdown-toggle, .ProfileAvatar, .js-action-profile-avatar, .DashboardProfileCard-avatarLink {
      border-radius: 100px;
  }
}
