css:
@-moz-document domain("steamtrades.com") {


/*steamtrades 1.1.3*/

/*---home and shared styles*/

.page_heading_breadcrumbs, .page_heading_btn {
  background: linear-gradient(rgba(255,255,255,.8),rgba(255,255,255,.5));
  border: 0;
  color: #305AC9;
}

.table .header {
  border: 0;
  background: linear-gradient(rgba(255,255,255,.35),rgba(255,255,255,.25));
  color: #131828;
}

.table h3>a {
  color: #ddd;
}

.table h3>a:hover {
  color: #eee;
}

.table .row_outer_wrap {
  color: #A6BDD9;
}

.table .underline {
  color: #D0DDF0;
}

.table .underline:hover {
  color: #9CBBF5;
}

.table .row_outer_wrap:not(:last-of-type) {
  border-bottom: 1px solid rgba(255,255,255,.08);
  box-shadow: none;
}
    
.pagination {
  border-top: 1px solid rgba(255,255,255,.3);
  box-shadow: none;
  color: #CCD7F2;
}

.pagination_navigation a {
  color: #D8F1C9;
}

.pagination_navigation a:hover {
  color: #E3F5D8;
}

.pagination_navigation a.is_selected {
  color: #82D246;
}

.pagination_navigation a.is_selected:hover {
  color: #9CDC6D;
}

.pagination_navigation a:not(.is-selected) span {
  border-bottom: 0;
  box-shadow: none;
}

a {
  text-decoration: none;
  border-bottom: 0 !important;
  box-shadow: none !important;
  transition: .2s;
}

.footer_inner_wrap {
  padding: 15px 25px;
}

.notification {
  border: 0;
  text-shadow: none;
}

.header_search > div:first-child > div > div:last-child, 
.header_search > div:last-child > div > div:last-child {
  transition: .2s;
  text-shadow: none;
  box-shadow: none;
  color: #fff;
}

.header_search > div:first-child > div > div:last-child:hover {
  background: #1C99E3;
}

.header_search > div:first-child > div > div:last-child:active {
  background: #1C99E3;
}

.header_search > div:last-child > div > div:last-child:hover {
  background: #CF4F23;
}

.header_search > div:last-child > div > div:last-child:active {
  background: #CF4F23;
}



/*---trades---*/

.action_list, .comment_collapse_btn, 
.comment_expand_btn, 
.author_name:not(.is_op), 
.author_small, 
.author_permalink {
  text-shadow: none;
}

a.author_name:not(.is_op) {
  color: #BDD6F4;
}

a.author_name:not(.is_op):hover {
  color: #A6C8F0;
}

.trade_heading {
  color: #eee;
}

.markdown h1, 
.markdown h2, 
.markdown h3 {
    color: #82D246;
}

.comment_body_default, 
.comment_body_delete, 
.comment_body_collapse {
  color: #eee;
}

.action_list>div>span {
  color: #A6BDD9;
  background: rgba(0,0,20,.2);
  padding: 2px 5px 3px 5px;
  border-radius: 3px;
}

.action_list>div>span:nth-child(2) {
  margin-left: 3px;
}

.comments .comment_children {
  border-left: 1px solid rgba(255,255,255,.08) !important;
  box-shadow: none !important;
}

.comment_children>div {
  margin-top: 10px;
}

.comments > .comment_outer:not(:last-child) {
  border-bottom: 1px solid rgba(255,255,255,.16);
  box-shadow: none;
}

.markdown a {
  color: #7BBEF6;
  text-decoration: none;
  transition: .2s;
}

.markdown a:hover {
  color: #7BD8F6;
}

.markdown a:visited {
  color: #AE94E0;
}

.sidebar > .heading, 
.reply_form > .heading {
  color: #ACB8D7;
  text-shadow: none;
}

.btn_action.white {
  color: #aaaaaa;
  border: 0;
  background: #1C2237;
  text-shadow: none;
  transition: .2s;
}

.btn_action.white:hover:not(.is_saving):not(.is_disabled):not(:active) {
  color: #eeeeee;
  background: #305AC9;
}

.btn_action.white:active:not(.is_saving):not(.is_disabled) {
  background: #305AC9;
  color: #eeeeee;
  box-shadow: none;
  text-shadow: none;
  margin-top: 0;
  margin-left: 0;
  border: 0;
}

.btn_cancel>span {
  border-bottom: 0;
  box-shadow: none;
  color: #aaaaaa;
}

.btn_cancel {
  margin-left: 5px;
  border-radius: 4px;
  padding: 0px 12px;
  background: #1C2237;
  transition: .2s;
  font: 700 13px/32px "Open Sans",sans-serif;
}

.btn_cancel:hover {
  background: #6C6C6C;
}



/*---new trade---*/

form .heading {
  text-shadow: none;
}

form .heading .name, 
form .heading .number, 
form .heading .optional {
  color: #DBE2F7;
}

.btn_action.green {
  color: #eee;
  border: 0;
  background: #3FAF38;
  text-shadow: none;
  transition: .2s;
}

.btn_action.green:hover:not(.is_saving):not(.is_disabled):not(:active) {
  color: #3FAF38;
  background: #1A2537;
}

.btn_action.green:active:not(.is_saving):not(.is_disabled) {
  background: #1A2537;
  color: #3FAF38;
  box-shadow: none;
  text-shadow: none;
  margin-top: 0;
  margin-left: 0;
  border: 0;
}



/*---pop up---*/
.popup {
  background: radial-gradient(#414863,#1C1F2B);
  border-radius: 6px;
  text-shadow: none;
  box-shadow: 0 0 15px 0 rgba(0,0,0,.3);
}

.popup_heading_h1 {
  color: #D2D7E3;
}

.popup_heading_h2 {
    color: #DCDFE9;
}

.popup_actions {
  color: #222;
}

.popup_actions > * {
  background: rgba(220,225,235,.6);
  border-radius: 4px;
  padding: 3px 6px;
  border-bottom: 0;
  box-shadow: none;
  transition: .2s;
}

.popup_actions > *:hover {
  background: rgba(220,225,235,1);
}



/*---user---*/

.sidebar_table > div:not(:last-child) {
  border-bottom: 1px dotted rgba(255,255,255,.2);
}

.sidebar_table > div:not(:first-child) {
  border-top: 0;
}

.sidebar_table {
  color: #9AA6BA;
  text-shadow: none;
}

.sidebar_table a {
  color: #7A96DE;
  border-radius: 3px;
  background: rgba(0,0,0,.3);
  padding: 3px 5px 3px 4px;
  transition: .2s;
}

.sidebar_table a:hover {
  background: rgba(0,0,0,.6);
}

.btn_action.red {
  color: #eee;
  border: 0;
  background: #851e1f;
  text-shadow: none;
  transition: .2s;
}

.btn_action.red:hover:not(.is_saving):not(.is_disabled):not(:active) {
  color: #CE2F2F;
  background: #1A2537;
}

.btn_action.red:active:not(.is_saving):not(.is_disabled) {
  background: #1A2537;
  color: #CE2F2F;
  box-shadow: none;
  text-shadow: none;
  margin-top: 0;
  margin-left: 0;
  border: 0;
}

.review_rating.is_positive > div {
  background: #3FAF38;
  text-shadow: none;
  color: #eee;
  border-radius: 0 3px 0 9px;
}

.review_rating.is_negative > div {
  background: #851e1f;
  text-shadow: none;
  color: #eee;
  border-radius: 0 3px 0 9px;
}

.rating_checkbox {
  color: #6b7a8c;
  background: #1C2237;
  padding: 4px 6px;
  border-radius: 3px;
  transition: .2s;
}

.rating_checkbox:hover {
  background: #171D2F;
}

.page_flex > div:not(:first-child) {
  border-left: 1px solid rgba(255,255,255,.3);
  box-shadow: none;
}

.profile_summary {
  border-bottom: 1px solid rgba(255,255,255,.3);
  box-shadow: none;
}

.action_list>span>span {
  color: #A6BDD9;
  background: rgba(0,0,20,.2);
  padding: 2px 5px 3px 5px;
  margin-top: -2px;
  border-radius: 3px;
}

.action_list>span>span:nth-child(2) {
  margin-left: 3px;
}



/*---vote---*/

.btn_action.grey {
  color: #5D636F;
  border: 0;
  background: #AEB5C5;
  text-shadow: none;
  transition: .2s;
}

.btn_action.grey:hover:not(.is_saving):not(.is_disabled):not(:active) {
  color: #AEB5C5;
  background: #5D636F;
}

.btn_action.grey:active:not(.is_saving):not(.is_disabled) {
  background: #5D636F;
  color: #AEB5C5;
  box-shadow: none;
  text-shadow: none;
  margin-top: 0;
  margin-left: 0;
  border: 0;
}



/*---faq---*/

.notification.yellow {
  border: 0;
}

.markdown hr {
  border: 0;
  border-bottom: 1px solid rgba(255,255,255,.3);
}

.markdown .view_attached {
  text-decoration: none;
  color: #FF9A6F;
  transition: .2s;
}

.markdown .view_attached:hover {
  color: #FFCC9D;
}



/*---tables---*/

.markdown table {
  border: 1px solid rgba(255,255,255,.3);
}

.markdown thead {
  background: rgba(255,255,255,.15);
  border-bottom: 1px solid rgba(255,255,255,.3);
}

.markdown td:not(:last-child), .markdown th:not(:last-child) {
  border-right: 1px solid rgba(255,255,255,.3);
}

.markdown tr:not(:last-child) {
  border-bottom: 1px solid rgba(255,255,255,.3);
}



/*---notifications---*/

.nav_btn {
  position: relative;
}

.message_count {
  padding: 0;
  position: absolute;
  right: -4px;
  top: -10px;
  z-index: 1000;
  display: inline-block;
  font-size: 11px;
  color: #fff;
  width: 23px;
  height: 23px;
	text-align: center;
	line-height: 23px;
	border-radius: 50%;
  background: linear-gradient(45deg,#f00,#f90);
  background-size: 400% 400%;
  animation: messagenoti 2s ease infinite;
  box-shadow: 0 0 15px rgba(0,0,0,.3);
}

@keyframes messagenoti { 
  0%{background-position:0% 50%}
  50%{background-position:100% 50%}
  100%{background-position:0% 50%}
}



/*~~~color scheme~~~*/
/*[[color-sc]]*/


}
