css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tv.yandex.ru") {


body, html {background: black !important;   background-color: #000 !important; color: #FFF !important; } 
   .b-content, .b-tv-channel-content__schedule, .b-tv-recommended_type_channel, .b-tv-channel-content__channel-info, .b-tv-recommended, .footer, .header2__main,
.header, .content, .grid-event {background: #1A1919 !important;   color: #fff !important}
  .tv-filters__wrap, .filters {background: #323232 !important;   color: #fff !important}
  .popup__description {color: #000 !important}
  .tv-channel-title__text, .grid-channel__title, .recommended-bottom-item__title, .program-quotes__link  { color: #FFF !important}

  .tv-event_genre-match_no, .tv-event.tv-event_theme_past  { color: #75638C !important}
  .tv-event_genre-match_yes  .tv-event__time { background: #000 !important }

.tv-filter-days__item, .tv-filter-days__link.link, .tv-filter-types__button.button, .b-tv-channel-content__text { color: #FFF !important; }
  
.tv-event_genre_films, .button__label, .grid-event_match.grid-event_type_films .grid-event__item-link {  background: rgba(5, 10, 12, 0.87); color: #F6CD6A !important}
   .tv-event_genre_for-children, .grid-event_match.grid-event_type_for-children .grid-event__item-link { background: rgba(5, 10, 12, 0.87); color: #09a209 !important }
   .tv-event_genre_sport, .grid-event_match.grid-event_type_sport .grid-event__item-link { background: rgba(5, 10, 12, 0.87); color: #c61818 !important }
   .tv-event_genre_series, .grid-event_match.grid-event_type_series .grid-event__item-link { background: rgba(5, 10, 12, 0.87); color: #2D82D5 !important }
 .tv-event_genre-match_yes  { background: #000 !important }

  .tv-event, .grid-event__item-link { color: gray !important; }

 .calendar__item.calendar__item_weekend, .program-details__link  { color: red !important}
  .channel-schedule__event, .channel-recommended__title, .program-others__name, .program-persons__name { color: green !important}
  .calendar__item, .program-posts__name  { color: gray !important; }
  
}
