css:
@-moz-document domain("searx.me"), domain("search404.io") {
.result{
    display: flex;
    flex-flow:column wrap;
    margin-bottom:23px;
}
h4.result_header a{
    font-size:18px;
    order:1;
    color:#2900a2;
}
h4.result_header a:visited{
    color: /*[[viscolor]]*/ !important;
}
.text-muted{
    order:2;
    color:#006621;
    font-size: 14px;
    float:left;
    display:inline;
    position:relative;
}
.pull-right{
    order:2;
    position:absolute;
    right:0;
    margin-right:30px;
}
.result-content{
    order:3;
}
.text-info{
    font-size:11px;
	display: none;
}
}
