css:
@-moz-document domain("paper.dropbox.com") {
/*
 * Theme for Dropbox Paper - Dark Gray(Dropbox Paper)
 * Author  : Po Chun(Sky)
 * License : Public Domain
 * Created : Nov. 04, 2016
 * Updated : Mar. 26, 2018
 */


/***********************************************
 * Background - Color
 ***********************************************/

/* body & header & toolbar & table & bottom-right button & Task Button: background color */
body,
#main-header-container,
.main-header-right,
.insert-rich-object-toolbar-wrapper .insert-toolbar-overlay,
.ace-table,
.table-v2--is-scrollable .table-v2,
.hp-versions-button,
.insert-toolbar-preview-image,
.insert-button,
#hp-editor-hotkeys-button, #hp-editor-bug-button,
#hp-editor-exp-assist-buttons,
#hp-tasks-button,
a.dmc-button-secondary,
.hp-menu {
	background-color: /*[[bgColor]]*/;
}

/* input preview & code block & hp-menu list(:hover) :background color */
.insert-toolbar-preview-image .insert-toolbar-preview-image-inner,
.insert-toolbar-preview-image:hover .insert-toolbar-preview-image-inner ,
.ace-line .insert-toolbar-preview,
.ace-editor .line-list-type-code,
code.listtype-code .insert-toolbar-preview-input,
.hp-menu-standard>ul>li .hpx-button:hover {
	background-color: /*[[previewColor]]*/;
}

/* Task List (Hover) : background color & font color*/
@media (min-width: 600px) {
    .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-task.focused-line, 
    .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-taskdone.focused-line, 
    .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-task.ace-task-line-highlighted, 
    .ace-feature-due-dates .zoneId-0>.ace-line.line-list-type-taskdone.ace-task-line-highlighted {
		background-color:  /*[[previewColor]]*/!important;
        color: /*[[fontColor]]*/!important;
	}
}

/* main content & left sidebar & insert toolbar */
.ace-editor ,
.hp-toc .hp-toc-entries,
.right-insert-toolbar-wrapper .insert-toolbar-overlay {
    background: none;
}


/* header : baseline */
#main-header-container {
	box-shadow: 0 1px rgba(255,255,255,0.5);
}


/***********************************************
 * Font - Family & Color
 ***********************************************/

/* main content : font family */
.ace-editor,
.ace-feature-bigtitle>.ace-editor>div:first-child {
    font-family: /*[[fontFamily]]*/,AtlasGrotesk-editor,sans-serif;
}

/* main content & code & task due date font: font color */
body,
.ace-editor,
.hp-toc .hp-toc-entry-link,
.hp-versions-button,
.hp-header-title,
.dmc-button-secondary,
.ace-editor code.listtype-code,
.insert-button,
.ace-line-task-controls-due-date-button,
.hp-menu-standard>ul>li .hpx-button, 
.DayPicker {
	color: /*[[fontColor]]*/;
}

/* header > folder name of this paper : font color */
.hp-pad-folder a,
.hp-pad-folder a:active,
.hp-pad-folder a:link,
.hp-pad-folder a:visited {
    color: rgb(212, 212, 212);
}

/* left sidebar > active link : font color */
.hp-toc .hp-toc-entry-current .hp-toc-entry-link,
.hp-toc .hp-toc-entry:hover .hp-toc-entry-link {
    color: rgb(22, 255, 9);
}

/* Comment font highlight : font color */
body.show-comments span.attrcomment,
body.underline-comments span.attrcomment{
	border-bottom-color: rgb(50, 166, 89);
    background-color: /*[[comHighlightColor]]*/!important;
}

/* highlight : font color & custom bg color*/
.highlight, .highlight.code, .highlight.inline-code {
    color: /*[[bgColor]]*/;
    background-color: /*[[highlightColor]]*/;
}

/* icon color */
.dmc-button .svg-icon {
    fill: /*[[iconColor]]*/;
}

/***********************************************
 * Others
 ***********************************************/

/* hide the scroll-bar of left sidebar */
.hp-toc::-webkit-scrollbar {
    display: none;
}

.right-insert-toolbar-wrapper .insert-toolbar-overlay::before {
	background: none;
}

}
