css:
@namespace url(http://www.w3.org/1999/xhtml);

/*---------------------------*/
/*   傲游哈哈广告过滤+页面美化   */
/*-------------------------- */

@-moz-document domain('haha.mx'){
  div#recommend-topic,
  div#joke-list-banner,
  div[class^="ad-"], div[class^="banner-ad-"],
  div#top-10,
  div.footer-hezuo
  {display:none!Important;}
}

@-moz-document url-prefix('http://www.haha.mx/joke/'), url-prefix('https://www.haha.mx/joke/'){
  div.sidebar{display:none!important;} 
  div#main{width:auto!Important;float:none!important;}
  /*内容居中*/
  div.joke-main-content{text-align:center;}
  /*内容字号*/
  div.joke-main-content>p{font-size:16px!important;} 
  /*蘑菇推荐——图片大小调整*/
  div.recommand-joke-main-list-thumbnail>.joke-item{width:95px!important;}
}

@-moz-document url-prefix('http://www.haha.mx/joke/'), url-prefix('https://www.haha.mx/joke/'){
  /* 如不需隐藏，请自行移除该条规则 */
  /*笑话页面——评论隐藏*/
  div.joke-comment-list-wrapper{display:none!Important;}
  /*笑话页面——蘑菇推荐隐藏*/
  div.leftside-recommendation{display:none!Important;}
}
