css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kuleczky.pl") {
  .ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #67c1f5;
    color: #333333;
    border-radius: 5px 5px 0px 0px;
}
  .ipsNavBar_primary:not(.ipsNavBar_noSubBars) > ul::before {
    background: #67c1f5;
    border-radius: 5px 5px 5px 5px;
}
  .ipsNavBar_secondary {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 40px;
    z-index: 1000;
    display: none;
    width: 100%;
    background: #67c1f5;
    margin: 0 auto;
    border-radius: 5px 5px 5px 5px;
}
  .ipsNavBar_secondary a {
    color: #1f2530;
}
  .ipsNavBar_secondary > li > a:hover {
    color: #fff;
}
  #ipsLayout_contentWrapper {
    background: transparent;
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
    margin: 0 auto;
}
  .ipsApp .ipsButton_primary {
    font-weight: 400;
    border: 2px solid;
    border-color: #67c1f5;
    background: url("http://www.kuleczky.pl/majk/belkaips4.png");
    color: #67c1f5;
    text-shadow: 1px 1px 2px #333333;
    font-weight: bold;
}
  .ipsWidget.ipsWidget_vertical .ipsWidget_title {
    font-family: "Motiva Sans Light", "Motiva Sans", arial, tahoma, sans-serif;
    font-weight: normal;
    font-size: 14px;
    color: #67c1f5;
    margin-bottom: 10px;
    border-bottom: 1px solid #67c1f5;
    padding: 11px 15px;
    text-align: center;
    text-shadow: 1px 1px 2px #333333;
    font-weight: bold;
    border-radius: 10px 10px 0px 0px;
}
  .ipsType_sectionTitle {
    border: 0px;
    color: #67c1f5;
    text-shadow: 1px 1px 2px #333333;
    font-weight: bolder;
    padding: 14px;
    font-size: 10px;
    letter-spacing: 1px;
    text-transform: uppercase;
    border-top: 0px solid rgba(255,255,255,0.15);
    overflow: hidden;
    transition: all 0.5s;
    border-radius: 10px 10px 0px 0px;
}
  a.ipsType_sectionTitle, .ipsType_sectionTitle a {
    color: #67c1f5;
    text-shadow: 1px 1px 2px #333333;
    font-weight: bolder;
    font-size: 18px;
    text-transform: uppercase;
    transition: color 0.25s ease-in-out 0s;
    border-radius: 10px 10px 0px 0px;
}
  .ipsItemStatus.ipsItemStatus_large {
    background: #67c1f5;
    color: #16202d;
    box-shadow: 1px 1px 2px #333333;
}
  .ipsRepBadge_positive, .ipsRepBadge_positive:hover {
    color: #67c1f5 !important;
    text-shadow: 1px 1px 2px #333333;
}
  .ipsUserPhoto_large img, img.ipsUserPhoto_large, .ipsUserPhoto_large::after {
    width: /*[[wielkoscavatar]]*/;
    height: /*[[wielkoscavatar]]*/;
    border: 2px solid /*[[kolorekavatarow]]*/;
    border-radius: /*[[ksztaltavatara]]*/;
    box-shadow: 1px 1px 2px #333333;
}
  .ipsPip {
    font-size: 8px;
    color: #67c1f5;
    text-shadow: 1px 1px 2px #333333;
}
  hr.ipsHr {
    border-bottom-color: #67c1f5 !important;
    border-bottom-style: dashed;
    border-bottom-width: 2px;
    border-top-color: #67c1f5 !important;
    border-top-style: solid;
    border-top-width: 1px;
}
  .ipsType_light {
    color: /*[[kolortekstu]]*/;
}
  .ipsApp .ipsButton_fullWidth {
    border: 1px solid;
    border-color: #67c1f5;
    border-radius: 15px;
    background: url("http://www.kuleczky.pl/majk/belkaips4.png");
    color: #67c1f5;
    text-shadow: 1px 1px 2px #333333;
    font-weight: bold;
}
  .ipsItemStatus:not(.ipsItemStatus_large) {
    color: #67c1f5;
    font-size: 10px;
    text-shadow: 1px 1px 2px #333333;
}
  .ipsBadge_positive, .ipsBadge_style4 {
    background: #67c1f5;
}
  .fa-thumb-tack::before {
    color: #16202d;
}
  .ipsTag_prefix {
    background: #67c1f5;
    color: #16202d !important;
}
  html[dir="ltr"] .ipsTag_prefix::before {
    border-color: transparent #67c1f5 transparent transparent;
}
  .ipsTags a::after, .ipsTag_prefix::after {
    background: #16202d;
}
  .fa-star::before {
    color: #16202d;
}
  .buzz-out {
    visibility: collapse;
}

/*[[elUserNav]]*/

  .ipsUserPhoto_tiny img, img.ipsUserPhoto_tiny, .ipsUserPhoto_tiny::after {
    border-radius: 50px;
    box-shadow: 0px 0px 5px 1px rgba(87, 193, 245, 0.1);
}
  #elUserNav > li > a {
    font-weight: bold;
}
  body {
    background-image: url('/*[[wlaczwylacztlo]]*/');
}

