/*[[topspamerzy]]*/

#backtoTop {
    bottom: 20px;
    color: #ffffff;
    opacity: 0.3;
}

#backtoTop:hover {
    bottom: 20px;
    color: #67c1f5;
    opacity: 1;
}
#elChatbox {
    border-bottom: 1px solid #67C1F5;
}
.cForumTitle {
    border-bottom: 1px solid #67C1F5;
}
.ipsType_sectionTitle {
    border-bottom: 1px solid #67C1F5;
}
.cProfileRepScore_negative .ipsType_minorHeading, .cProfileRepScore_positive .ipsType_minorHeading {
    color: #222222;
}
.cProfileRepScore_positive {
    background: #67c1f5;
    color: #222222;
}
.ipsButton.ipsButton_repUp {
    background: #67c1f5;
}
.ipsButton.ipsButton_repUp, .ipsButton.ipsButton_repDown {
    color: #222222;
}
.ipsType_success, .ipsType_positive {
    color: #222222;
}
.ipsReputation_count.ipsType_positive {
    background: #67c1f5;
}
.ipsType_neutral {
    color: #67c1f5;
}
.ipsReputation_count {
    background: #222222;
    border: 1px solid #67c1f5;
}
.ipsButton.ipsButton_repDown {
    background: #67c1f5;
}
.ipsApp .ipsDataItem_new, .ipsApp .ipsDataItem_success {
    background: #032536;
    box-shadow: inset 0px 0px 20px 5px #043249, inset 5px 0px 5px 1px #043249, inset -5px 0px 5px 1px #043249;
    border-right: 1px solid #053f5b;
    border-left: 1px solid #053f5b;
}
.ipsTabs {
    border-radius: 10px 10px 0px 0px;
}
.ipsImage_thumbnailed {
    border: 1px solid /*[[kolortekstu]]*/;
    border-radius: 15px;
}
.ipsWidget {
    border-radius: 10px 10px 0px 0px;
}
