css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {
  .PermalinkOverlay {
    overflow-x: hidden;
  }
  .PermalinkOverlay-modal {
    position: relative;
    top: 0!important;
    left: 0;
    margin: 60px 70px;
    width: auto;
    min-width: 640px;
  }
  .permalink-container {
    width: 100%;
    float: none;
  }
  .TweetArrows {
    width: auto;
    right: -80px;
  }
  .permalink-container .AdaptiveMedia {
    max-width: 100%;
    max-height: none!important;
    width: auto;
  }
  .permalink-container .AdaptiveMedia-container,
  .permalink-container .AdaptiveMedia-container div[class*=Photo],
  .permalink-container .AdaptiveMedia-container .AdaptiveMedia-photoContainer {
    display: inline-block;
    vertical-align: top;
    width: auto;
    height: auto;
    position: relative;
    padding: 0!important;
    margin: 0;
  }
  .permalink-container .AdaptiveMedia img {
    display: block;
    position: relative;
    top: 0!important;
    left: 0!important;
    width: auto!important;
    height: auto!important;
    max-width: 100%;
  }
  .permalink-container .AdaptiveMedia.is-generic-video {
    width: 100%;
  }
  .permalink-container .AdaptiveMedia-video {
    width: /*[[video-width]]*/px;
    max-width: 100%;
  }
}
