@-moz-document regexp("^https?://mastodon\.social(/web(/.*))?/?$") {
  
  div[data-reactroot] {
    background: none!important;
  }

  html, body {
    font-size: 12pt;
  }

  label, .drawer__inner > div:nth-of-type(2) {
    font-size: 10pt;
  }
  
  .status__content a {
    text-decoration: underline!important;
  }
  
  body, body.app-body {
    font-size: inherit;
    font-family: Iowan Old Style, Cardo, Junicode, EB Garamond, Garamond, serif;
    font-variant-numeric: oldstyle-nums;
    hyphens: auto;
    quotes: "‘" "’" '“' '”';
    line-height: 1.35;
    text-align: left
  }

  .column > div:first-child:not(.static-content), .drawer__header {
    margin: 0!important;
    border-bottom: thin currentColor solid;
    font-family: IM FELL Double Pica PRO, IM Fell Double Pica, Iowan Old Style, Cardo, Junicode, EB Garamond, Garamond, serif;
    font-size: 1.75rem!important;
    font-weight: bold!important;
    line-height: 1!important;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
  }
  
  .static-content h1 {
    margin: 0 0 1rem!important;
    font-size: 1.5rem!important;
    font-weight: bold!important;
    line-height: 1!important;
  }

  .drawer input {
    margin: .1em!important;
    border: thin currentColor solid!important;
    padding: 0 1.8em 0 .8em!important;
    color: inherit!important;
    font-size: inherit!important;
    font-family: inherit!important;
    font-weight: inherit!important;
    line-height: inherit!important;
  }

  .drawer__inner > div:first-child > div:nth-of-type(2) {
    margin: 0!important;
    padding: 0!important;
    top: .1rem!important;
    right: .8rem!important;
    font-size: inherit!important;
    font-weight: inherit!important;
  }

  .react-autosuggest__suggestions-container {
    box-shadow: none;
  }

  .react-autosuggest__section-title {
    margin: .1rem!important;
    padding: 0!important;
    text-align: center;
    font-size: inherit;
    font-weight: inherit;
    line-height: inherit;
    text-transform: none;
  }

  .react-autosuggest__suggestions-list {
    margin: 0;
    padding: 0;
  }

  .react-autosuggest__suggestion {
    margin: .1em;
    border: thin currentColor solid;
    padding: 0 .8rem;
  }

  .drawer .react-autosuggest__section-title strong {
    font-weight: inherit!important;
  }

  .autosuggest-textarea__textarea {
    border: thin solid currentColor!important;
    padding: .5rem!important;
    font-size: .85rem!important;
    font-family: inherit!important;
    line-height: inherit!important;
    color: inherit!important;
  }

  .columns-area {
    margin: 1rem;
  }

  .drawer {
    margin: 0 auto 0 0;
    border: medium solid currentColor;
    font-size: .85rem;
  }

  .drawer__inner {
    padding: .5rem!important;
  }

  .drawer * {
    color: inherit!important;
  }

  .drawer strong {
    font-weight: bold!important;
  }

  .drawer button {
    margin: .1em!important;
    background: #DDDDDD!important;
    border: thin currentColor solid!important;
    border-radius: 0!important;
    padding: 0 .8em!important;
    font-size: inherit!important;
    font-family: inherit!important;
    font-weight: inherit!important;
    line-height: inherit!important;
    font-variant-caps: titling-caps!important;
    width: auto!important;
    height: auto!important;
  }

  .drawer__inner > div {
    margin: .5rem 0!important;
    padding: 0!important;
  }

  .drawer__inner > div:nth-of-type(1) {
    position: relative;
    margin: 0!important;
    padding: 0!important;
    line-height: inherit;
  }

  .drawer__inner > div:nth-of-type(3) > div:not(:first-child) div {
    line-height: inherit!important;
    padding: 0!important;
  }

  .react-toggle .react-toggle-track {
    background: currentColor;
  }

  .react-toggle:hover:not(.react-toggle--disabled) .react-toggle-track {
    background: currentColor;
  }

  .column {
    margin: 0 .5rem;
    border: medium solid currentColor;
  }

  html .column *:not(i):not(.active) {
    color: inherit!important;
  }
  
  .column div {
    background-color: transparent!important;
  }
  
  .column-back-button {
    color: inherit!important;
  }
  
  .notification > div:first-child {
    color: inherit!important;
  }

  .column strong {
    font-weight: bold!important;
  }

  .column:last-child {
    margin-right: auto;
  }

  p {
    text-align: justify;
  }
  
  /* Begin theme code: */
  
  /*[[themecode]]*/

}
