css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
  body{
    background-color: #fafafa;
    font-family: roboto;
    border: 1px solid grey;
    border-radius: 5px;
    padding: 10px;
    box-shadow: 1px 1px 2px grey;
    background: transparent url("https://subtlepatterns.com/patterns/crossword.png") repeat scroll 0% 0%;
  }
  a{
    text-decoration: none;
  }
  p{
    line-height: 1.5em;
    font-size: 15px;
    text-align: justify;
  }
  ul{
    list-style-type: none;
    padding: 0;
  }
  #main-header{
    text-align: center;
    background-color: /*[[hcolor]]*/;
  }
  
  
  
  #front-page-best h2,#front-page-newest h2{
    text-align: center;
    margin: -25px 0 0 0;
    background-color: #fafafa;
    
  }
  
  #front-page-best h2{
    width: 85px;
  }
  
  #front-page-newest h2{
    width: 115px;
  }
  #front-page-best, #front-page-newest{
    margin-top: 20px;
    border: 1px solid grey;
    border-radius: 5px;
    padding: 15px 20px;
    box-shadow: 1px 1px 2px grey;
    margin-left: 50px;
    line-height: 19px;
    background-color: #fafafa;
  }
}
