css:
@namespace url(http://www.w3.org/1999/xhtml);

/*

  Print friendly for Google+ Article

  Author: bakcy0175
  Date: 2016/12/13

*/

@-moz-document regexp('https://plus\\.google\\.com/.*/posts/.*') {
  
  @media print {
    
    .bhUHJf,
    .pWgqe
    {
      display: none !important;
    }
  
    .xO1bNWe,
    .DAbEod
    {
      margin: 0px !important;
      position: absolute !important;

      /* left margin, default 0px */
      margin-left: /*[[leftMargin]]*/ !important;      
    }
    
  .LHyahc
    {
      /* article width, default 135% */
      width: /*[[articleWidth]]*/ !important;      
    }
  
  }
  
}
