@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("weibo.com") {

  div.gn_topmenulist_tips {
    padding: 0 3px !important;
    border: 0 !important;
    overflow: hidden;
  }
  
  div.gn_topmenulist_tips>ul>li {
    display: block;
    padding: 0 30px 0 13px !important;
    line-height: 0 !important;
    overflow: hidden;
  }
  
  div.gn_topmenulist_tips>ul>li>a {
    display: inline-block;
    margin-top: 8px;
    height: 22px;
  }
  
  div.gn_topmenulist_tips>a {
    top: 7px !important;
    right: 11px !important;
  }
  
  div.gn_topmenulist_tips>ul>li:first-child>a {
    margin-top: 22px;
  }

  div.gn_topmenulist_tips a[href="http://weibo.com/tv"],
  div.gn_topmenulist_tips a[href="http://d.weibo.com"] {
    display: none;
  }
}
