css:
@-moz-document domain("medium.com") {
.postArticle--full .sectionLayout--insetColumn {
    max-width: calc(100% - /*[[margins]]*/px * 2);
    padding-left: /*[[margins]]*/px;
    padding-right: /*[[margins]]*/px;
}
.postArticle-content .graf--p,
.graf--li {
    font-size: 16px;
}
.postMetaHeader,
.js-postShareWidget,
.paragraphControls,
.responsesStreamWrapper,
.postActions,
::-webkit-scrollbar {
    display: none;
}
::selection {
  background: #9BFFB3;
}
::-moz-selection {
  background: #9BFFB3;
}
}
