/*[[tumblrframe]]*/


/*[[submit]]*/

@-moz-document url('http://artist-grapevine.tumblr.com/blacklist') {
#youngjae .texto p,#youngjae .texto ul{
color:#c7bbbd!important;
}
}

@-moz-document url-prefix('http://artist-grapevine.tumblr.com/image/') {
body::after {
background-image:none!important;
}
}
