css:
@-moz-document regexp("https://www.douyu.com/[0-9a-zA-Z]+"), regexp("https://www.douyu.com/topic/[0-9a-zA-Z_-]+(\\?[0-9a-zA-Z_-]+=[0-9a-zA-Z_-]+)?") {
body{
    min-width: auto !important;
}
@media screen and (max-width: 1366px){
    .layout-Main{
		max-width: 999999px !important;
	}
}
.layout-Container{
	padding-top: 0 !important;
}
.layout-Container--hasAside{
    padding-top: 0 !important;
}
.layout-Main{
    margin-left: 0 !important;
    padding-top: 0 !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
}
#js-player-barrage{
    top: 0 !important;
}
.PlayerToolbar-Wealth{
    width: auto !important;
}
.Title {
    padding-left: 10px !important;
}
.RewardModal.super,
.js-player-toolbar,
.Title,
.layout-Player-toolbar{
    height: auto !important;
}
.ChatSend-txt {
    width: 262px !important;
    padding: 5px !important;
}
.RewardModal.super{
    background: auto !important;
}

div.ToolbarActivityArea>div:not(:first-child){
    display: none !important;
}
#js-room-activity,
.PlayerToolbar-GiftWrap,.PlayerToolbar-signCont,
.layout-Player-rankAll,.layout-Player-rank,
.FansMedalPanel-container,
.layout-Bottom,.RewardModal-recommendList,.RewardModal-text,
.vcode9-sign,.layout-Aside,
.layout-Header,.layout-Banner,
.ChatBarrageCollect, .Title-roomOtherBottom,
.Title-impress, .Title-anchorPic, .PlayerToolbar-Task,
.MatchSystemMedalPanel-container,
.PlayerToolbar-ycInfo, .PlayerToolbar-getYCArea{
    display: none !important;
}
}
