css:
@-moz-document domain("planfix.ru"), domain("planfix.com"), domain("planfix.ua") {
/*
ПланФикс - Комментарий, кнопки вверху (by Kirr)
2018-11-14
*/


.task-card-wrapper > #action-create-div {
    border-top: #e0e0e0 solid 3px;
    padding-top: 50px;
    position: relative;
}

.tr-action-commit > td.td-item {
    position: absolute;
    top: 5px;
    left: 0px;
}

#newActionForm tr.action-change-dates + tr > td:first-child {
    display: none;
}

#newActionForm tr.action-change-dates + tr > td.td-item-second {
    position: absolute;
    top: 16px;
    left: 356px;
}


.tr-actions-body-editable.actionwr.tr-actions-body-new.tr-actions-body-edit-form .td-actions-wrapper {
    position: relative;
    padding-top: 54px !important;
    padding-bottom: 5px;
}

.tr-actions-body-editable.actionwr.tr-actions-body-new.tr-actions-body-edit-form .td-actions-wrapper table table tbody > tr:last-child > td {
    position: absolute;
    top: 0px;
    left: 0px;
}

.tr-actions-body-editable.actionwr.tr-actions-body-new.tr-actions-body-edit-form .td-actions-wrapper .tr-action-attach-anchor > td:first-child {
    display: none;
}

.tr-actions-body-editable.actionwr.tr-actions-body-new.tr-actions-body-edit-form .td-actions-wrapper .tr-action-attach-anchor > td.td-item-second {
    position: absolute;
    top: 20px;
    left: 170px;
}

}
