css:
@-moz-document domain("haikulearning.com") {
body {
    background: url("/*[[background-img]]*/");
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}
#frame {
    max-width: 1230px;
    min-width: 960px;
    min-height: 88.5vh;
    margin: 0 auto 1em;
    padding: 0;
    -webkit-box-shadow: 0 0 5px rgba(80, 80, 80, 0.3) !important;
    -moz-box-shadow: 0 0 5px rgba(80, 80, 80, 0.3) !important;
    box-shadow: 0 0 5px rgba(80, 80, 80, 0.3) !important;
    background-color: rgba(255, 255, 255, 0.7) !important;
}
#header {
    background: rgba(255, 255, 255, 0) !important;
}
h1 { 
    color: rgb(0,45,64) !important;
}
#cms_page_eclass_name a {
    color: rgb(0,45,64) !important;
}

}
