css:
@-moz-document domain("php.net") {
  html {
    background: #777 url('http://php.net/images/bg-texture-00.svg') !important;
  }
  .navbar .navbar-search .search-query {
  	text-shadow: initial !important;
    color: #F5F5F5;
  }
  input[type='search'][placeholder='Search'] {
  	background: rgba(125, 125, 125, 0.15) !important;
    outline: none;
  }
  input[type='search'][placeholder='Search']::-webkit-input-placeholder {
  	color: #bbb !important;
  }
  input[type='search'][placeholder='Search']::-moz-placeholder {
    color: #bbb !important;
  }
  input[type='search'][placeholder='Search']:-ms-input-placeholder {
    color: #bbb !important;
  }
  input[type='search'][placeholder='Search']:-moz-placeholder {
    color: #bbb !important;
  }
  #usernotes .note .text, .example-contents > [class$="code"], .example-contents.screen,.informalexample .literallayout {
    background-color: #fafafa;
  }
  h2,h3:not(.result-heading),.title a,.title, h2.newstitle a {
    color: #000;
  }
  h1,h2.title,h1.title,h1.refname {
    color: #4cc373;
  }
  #usernotes .count {
    background-color: #4cc373;
  } 
  .tt-suggestion {
    background: rgb(212, 212, 212);
    border-bottom: 1px solid #E0E0E0;
  }
  .tt-suggestion.tt-is-under-cursor {
    background-color: #000000;
  }
  .tt-suggestion.tt-is-under-cursor {
    background-color: #FFC107;
  }
  .tt-dropdown-menu .result-heading {
    color: #f6f6f6;
    background-color: #BDBDBD;
    border-bottom: 2px solid #9E9E9E;
  }
  .tt-dropdown-menu {
    background: none repeat scroll 0 0 #d4d4d4;
    border-bottom: 1px solid #9e9e9e;
    box-shadow: none !important;
  }
  .tt-dropdown-menu .search {
    color: white;
    background: #616161;
  }
  .doctable th, .segmentedlist th {
    background-color: #ffc107;
  }
  .layout-menu ul.child-menu-list li.current a {
    color: #52da5d;
    border-bottom-color: #52da5d;
  }
  #layout-content a:link, #layout-content a:visited {
    color: #FF9800/*#aaa*/;
  }
  aside.tips a:hover, aside.tips a:focus {
    color: #FFC107;
    border-color: #FFC107;
  }
  a:hover, a:focus, #layout-content a:hover, #layout-content a:focus {
    color: #FFC107;
    border-color: #FFC107;
  }
/*[[theme]]*/
}
