css:
@-moz-document domain("reddit.com") {

/* unhide downvote buttons */
.down, body:not(.subscriber) .down, .thing .arrow.down {
  display: inherit !important;
  visibility: visible !important;
}
.down .collapsed, body:not(.subscriber) .collapsed .down {
  display: none !important;
  visibility: hidden !important;
}

/* remove seo recommended links */
.seo-comments, .seo-comments-recommendations {
  display: none !important;
}

/* remove trash */
[data-event-action="gild"], .listingsignupbar, .redesign-beta-optin {
  display: none !important;
}
.commentsignupbar {
  visibility: hidden !important;
}

}
