css:
@-moz-document domain("wikia.com"), domain("fandom.com") {
/*fixed top navigation bar*/
div#globalNavigation.wds-global-navigation {
	position: relative
}

/*hidden wikia bloat and ads*/
div#WikiaTopAds, div#TOP_LEADERBOARD_AB, div#WikiaRailWrapper, div#WikiaBar, section.module.discussion-module.discussion-test, div#WikiaArticleBottomAd, footer#WikiaFooter, footer.wds-global-footer {
	display: none!important
}

/*full width main article*/
article#WikiaMainContent {
	width: 100%!important
}
}
