css:
@-moz-document domain("blogger.com") {

div.postsNew, html, .PU2B2J-b-q, body {background: /*[[wheats]]*/;}

iframe#postingComposeBox, #postingComposeBox, body#postingComposeBox {
background: /*[[wheats]]*/; 
background-color: /*[[wheats]]*/ !important; 
padding: 10px;
font-family: "Libre Baskerville", Georgia, "Times New Roman", serif;
line-height: 1.3em;
color: /*[[composetext]]*/
}

#postingHtmlBox {
    font-family: monospace;
    background: #795548;
    color: white;
    font-size: 16px;
}

div.OMGM5KC-h-c, div.editorHolder, div.topHolder, div.optionHolder, .PU2B2J-b-a, .PU2B2J-b-g, .PU2B2J-m-b, .PU2B2J-m-h, .Xm-Xs-Md {background: /*[[greens]]*/;}

/* Various text elements */
.OYKEW4D-fc-f span, .K3JSBVB-dc-f span, span.OYKEW4D-S-f, .OYKEW4D-gc-f, a.OYKEW4D-S-b.blogg-title, a.K3JSBVB-Q-b.blogg-title, a.OYKEW4D-U-b.blogg-title, span.OYKEW4D-U-f, a.K3JSBVB-R-b.blogg-title, span.K3JSBVB-R-f 
{color: /*[[whitesmokes]]*/;}


/* remove top title bar */
header#gb {display:none;}

/* Blogger logo */
.gb_xd {background-color:#735246;}
span.gb_0a.gb_Za, .gb_sc {
background: whitesmoke; 
border-radius: 25px;}


/* Labels etc */
.OYKEW4D-ic-f span, .OYKEW4D-mc-f span, .OYKEW4D-mc-f, .K3JSBVB-ec-f, .K3JSBVB-Q-f, div.K3JSBVB-dc-d.selected, div.K3JSBVB-dc-d.selected:hover, div.K3JSBVB-dc-d.selected {color: /*[[wheats]]*/;}

label, .K3JSBVB-dc-b {color: black;}

/* Round corners for post compose box and extra space */
iframe#postingComposeBox {
    border-radius: /*[[corners]]*/px;
}
.editor .composeBoxWrapper {
    top: 20px;
    bottom: 20px;
}
body#postingComposeBox {
    width: auto;
}



/* End */
}
