css:
@-moz-document domain("stackoverflow.com") {
.top-bar ~ .container {
    margin-top: 37px;
}
.top-bar {
    border-top: none;
    background-color: #000;
    height: 36px;
}
.top-bar .-container {
    /*[[maxwidth]]*/
    width: auto;
    padding-left: 40px;
    padding-right: 40px
}
.top-bar .-logo {
    height: 36px
}
.top-bar .navigation .-link {
    color: #aaa;
}
.top-bar .navigation .-item._current .-link,
.top-bar .my-profile .-rep {
    color: #bbb;
    border-bottom: none;
}
.top-bar .navigation .-item._current .-link {
    color: white;
}
.top-bar .navigation .-link:hover {
    background-color: #333;
    color: white;
}
.top-bar .indicator-badge {
    border: none;
    font-weight: bold;
    font-size: 11px;
}
.top-bar .secondary-nav .-link._highlighted-reviews {
	color: #848d95;
}
.top-bar .secondary-nav .-link .indicator-badge:not(._regular),
.top-bar .secondary-nav .-link:hover .indicator-badge,
.top-bar .secondary-nav .-link .indicator-badge {
    top: 2px!important;
    right: -4px;
    z-index: 99;                            
}
.top-bar [role="icon"] {
    fill: #666;
}
.top-bar .my-profile:hover {
    background-color: #333
}
.top-bar .navigation .-item:nth-child(2) {
    display: none
}
.top-bar .navigation .-list,
.top-bar .navigation .-link,
.top-bar .secondary-nav .-list,
.top-bar .secondary-nav .-link {
    height: 36px;
    line-height: 36px
}
.top-bar .my-profile {
    padding-top: 6px;
    padding-bottom: 6px
}
.top-bar .searchbar input[type="text"].f-input {
    height: 24px;
    padding-left: 32px;
    background-color: #222;
    border: none;
    color: #aaa;
}
.top-bar .searchbar input[type="text"].f-input:hover {
    color: #bbb;
}
.top-bar .searchbar input[type="text"].f-input:focus {
    color: white;
    background-color: #444;
    box-shadow: none;
}
.top-bar .js-search-submit {
    background-color: #666!important;
    border-color: #444!important;
    height: 24px!important;
    min-height: 24px!important;
    box-shadow: none;
    padding: 2px 8px!important;
}
.top-bar .js-search-submit svg {
    fill: white;
}
.top-bar .searchbar>[role="icon"] {
    left: 20px
}
.top-bar .navigation .beta-badge {
    top: 24px;
    color: #777;
    font-weight: bold;
}
.top-bar .navigation li:hover .beta-badge {
    color: #aaa;
}
.top-bar .my-profile .-badges .badge2+.badgecount {
    color: #b6b8bb;
}
.top-bar .searchbar .btn-topbar-primary {
  border-color: #666 !important;
  box-shadow: none;
}
}
