css:
@-moz-document url-prefix("https://www.humblebundle.com/store") {
/* Entire store */
.rewards-gem-container {
    display: none !important;
}

/* Entire wishlist */
.storefront.v2 .wishlist-item-list li.wishlist-entity {
    width: 100%;
}
.storefront.v2 .wishlist-item-list div {
    flex-grow: 1;
}

.storefront.v2 .wishlist-item-list .entities-list {
  flex-direction: column;
}

.storefront.v2 .wishlist-item-list .wishlist-entity .entity-block-container {
  float: none;
}

/* Individual item */
.storefront.v2 .wishlist-item-list li.wishlist-entity {
    width: 100%;
}
.storefront.v2 .wishlist-item-list .entity {
    display: flex;
    flex-direction: row;
}

/* Image and Name wrapper */
.storefront.v2 .wishlist-item-list .entity-block-container a.entity-link {
    flex-grow: 3;
}
.storefront.v2 .wishlist-item-list .entity-details {
    display: flex;
}

/* Item Image */
.storefront.v2 .wishlist-item-list .wishlist-entity .entity-block-container .entity-image {
    width: auto;
    height: 130px;
    flex-grow: 0;
}

/* Item Name */
.storefront.v2 .wishlist-item-list .entity-block-container .entity-title {
    margin-top: 0;
    font-size: 1.5em !important;
	font-weight: normal;
    height: auto;
    line-height: 1.2em;
	text-transform: none;
}
.storefront.v2 .wishlist-item-list .entity-block-container .wishlist-button {
    position: inherit;
}

/* Buy Button */
.storefront.v2 .wishlist-item-list .entity-block-container .entity-purchase-details {
    max-width: 160px;
    align-items: flex-end;
    flex-direction: column;
    justify-content: flex-end;
}
.storefront.v2 .wishlist-item-list .entity-block-container .entity-devices {
    flex-grow: 0;
    margin-right: 2px;
    margin-bottom: 8px;
}
.storefront.v2 .wishlist-item-list .entity-block-container .entity-pricing, 
.storefront.v2 .wishlist-item-list .entity-container .entity-pricing {
    flex-grow: 0;
}

/* Floating Controls */
.wishlist-edit-actions {
    top: 0;
}
.move-wishlist {
    top: -2px;
    right: 40px;
}
.remove-wishlist {
    top: -2px;
    right: 4px;
}
}
