css:
@-moz-document domain("sms5.schoolsoft.se") {
/*=== GENERAL ITEMS ===*/

body {
	/*-- Some animateed background in case you want em--
	http://i.imgur.com/3kOcZBF.gif
	http://i.imgur.com/rc8Odvz.gif
	http://i.imgur.com/G6VRYwt.gif*/
	background: #181818 url("/*[[bg_image]]*/");
}

tr {
	border-radius: 0px;
}

a {
	color: #696969 !important;
}

/*=== MAIN PAGE ===*/

.colmask {
	position: absolute;
	left: 50%;
	width: 0px;
	margin-left: -25%;
}

#leftMenu {
	opacity: 0.9;
	background: #000;
	border: none;
	border-radius: 10px;
}

#leftMenu .menu_item_selected {
	background: #1e1e1e;
	color: white;
}

#leftMenu .menu_item {
	color: #696969;
}

#leftMenu .menu_item:hover {
	background: #1e1e1e;
}

#main {
	opacity: 0.9;
	background: #000;
	border: none;
	border-radius: 10px;
}

#main span {
	color: #000;
	/*font-family: 'Roboto', sans-serif;*/
	font-size: 12px;
}

#container {
	background: #000000;
}

#accordion {
	background: none;
}

.expand_image {
	width: 12px;
	height: 12px;
	content:url("http://i.imgur.com/0rbzqgQ.png");
}

.accordion-heading {
	background: none;
}

.accordion-group {
	background: none;
	border-color: #000;
}

.tinymce-p {
	color: #fff;
	font-size: 12px;
}

.h2 {
	color: #a8a8a8;
	text-shadow: 0 1px #000;
}

.h3 {
	color: #a8a8a8;
	text-shadow: 0 1px #000;
}

.h2_box {
	border: none;
	border-radius: 10px;
	background: #000;
}

.h2_inner {
	background: #1e1e1e;
	border-radius: 10px;
	border: none;
	color: #a8a8a8;
}

/*=== HEADER ===*/

#logo {
	display: none;
}

#menu {
	display: none;
}

#top {
    background: #1e1e1e;
}

#logout {
	float: right;
	text-align: center;
}

#logout a {
	border-bottom: none;
}

#logout a:hover {
	border-bottom: none;
	opacity: 0.5;
}

#header {
	background: #1e1e1e;
}

#header .divider {
	display: none;
}

#header .text {
	color: #a8a8a8;
	padding-right: 50px;
	padding-left: 20px;
}
}
