@-moz-document url("http://www.daum.net/"),regexp("http://www.daum.net/.*(raptor/|=refresh|nil_profile=).*"){
@font-face{font-family:'FontAwesome';font-weight:normal;font-style:normal;src:url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.eot?#iefix&v=4.3.0') format('embedded-opentype'),url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.woff2?v=4.3.0') format('woff2'),url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.woff?v=4.3.0') format('woff'),url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.ttf?v=4.3.0') format('truetype'),url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.svg?v=4.3.0#fontawesomeregular') format('svg')}

footer,article,#adMain,.top_service,.list_favorsch,h1.tit_daum>* {display:none!important}
h1.tit_daum  {background:url(http://cfile4.uf.tistory.com/image/195B1B1C4C6F52B6339B15)no-repeat!important;background-size:245px 105px!important;width:245px!important;height:105px!important;margin-left:calc(50% - 122.5px)!important;margin-bottom:20px}
.wrap_search {clear:both!important}
.wrap_search {margin:0!important}
.top_head {margin-top:200px!important}
#wrapSearch {margin-left:calc(50% - 223px)!important}
main {width:300px!important}
body,.bg_daum,#daumSuggestBox {background-image:none!important}
#loginForm,#daumContent {position:absolute!important;right:0;top:0}
#searchBar{background-image:none!important;border:3px solid #3f78df!important;background:white!important}
#searchSubmit:before{font-family:'FontAwesome';content: "\f002";font-size:18px;color:#3f78df}}

@-moz-document domain("search.daum.net"){
#premiumLinkColl,#premiumLinkColl+.line,#splinkColl,#splinkColl+.line,#wideLinkColl,#wideLinkColl+.line{display:none!important}
