css:
@-moz-document domain("www.facebook.com") {
/* Hide the stream */

#stream_pagelet {
    display: none;
}
/* Hide the sidebar */

#rightCol {
    display: none;
}
/* Hide notification count */

#fbNotificationsJewel .jewelCount {
    display: none;
}
/* Hide shortcut counters */

.sideNavItem .countValue {
    display: none !important;
}
/* Don't make notification icon white */

#fbNotificationsJewel a.jewelButton > div {
    background-position: 0 -188px !important;
}
/* Show a nice message */

#contentCol::before {
    display: block;
    content: '/*[[quote]]*/';
    margin: 24px auto;
    text-align: center;
    font-size: 1.25em;
    color: rgba(0, 0, 0, .5);
}
}
