css:
@-moz-document domain("vozforums.com"), domain("forums.voz.vn") {
body {
    padding-left: 0
}
.voz-living-side-menu:not(.voz-living-size-menu__bottom) {
    width: inherit!important;
    height: inherit!important;
}
.voz-living-side-menu .btn-group .btn {
    width: 40px!important;
    margin-right: 2px!important
}
.voz-living-side-menu div {
    display: inline-block;
    margin: 1px!important
}
.voz-living-side-menu div a {
    margin: 2px!important
}
.voz-living-side-menu .btn.tooltip-right.active {
    border-bottom-left-radius: 0px!important;
    border-top-left-radius: 14px!important;
    border-top-right-radius: 14px!important;
    padding-right: 0px!important;
    padding-bottom: 3px!important;
}
.voz-living-side-menu {
    background-color: /*[[color]]*/!important;
    opacity: 0.3!important;
    z-index: 15!important;
}
.voz-living-side-menu:hover {
    opacity: 1!important
}
.voz-living-side-menu div[class="voz-living-size-menu__bottom"] {
    position: fixed!important;
    bottom: 0!important;
    left: 0!important;
}
.btn-options {
    top: 45px!important;
    height: 75%!important;
}
div[data-tooltip="Options"]:after {
    z-index: 9999!important;
}
.voz-living-side-menu.trans-start {
    left: 0!important;
}
.voz-living-side-menu.compact .btn-group .btn {
    width: 30px!important;
    margin: 1px 1px!important;
}
.voz-living-side-menu.compact .btn-options {
    top: 33px!important;
}
.voz-living-side-menu.compact {
    padding: 0 2px;
}
.voz-living-side-menu.compact .btn-group a:not([data-tooltip]) {
    margin: 0 0 0 3px!important;
}
}
