css:
/* HEADER*/

@-moz-document url-prefix("https://dev.scrap.tf"), url-prefix("https://scrap.tf") {

:root {
    --globalFont: "Lato",Helvetica,Arial,sans-serif;    
    --radius: 0.2em;
    --avatarBorders: /*[[avatarBorders]]*/;
}

/*[[template]]*/

.raffle-message:not(.raffle-entered-msg),.raffle-password {
    opacity: 1!important;
    transition: inherit!important;
}
  
/* Custom scrollbar */

::-webkit-scrollbar-track{-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);border-radius: 10px;}::-webkit-scrollbar{width:12px;background:#202020 !important}::-webkit-scrollbar-thumb{border-radius: 10px;-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3) !important;background-color: #444 !important;}

body:not(.cke_editable):not(.cke_panel_frame){
    background-image: url(/*[[bg-image]]*/);
    background-size: /*[[bg-size]]*/;
    background-attachment: /*[[bg-attachment]]*/;
}
  
/* Better display for items on Create raffle */
  
.create-raffle-inv {
    overflow: auto;
    max-height: 750px;
    max-width: 920px !important;
}

/* Better comment section */

.auction-well .raffle-commenters, .raffle-well .raffle-commenters {
    max-height: 600px !important;
}

.raffle-commenters .comment-container {
    margin: 0px 10px !important;
    padding: 12px 10px;
    overflow: hidden;
    border-left: 2px solid transparent;
    border-top: 1px solid #efefef;
}

.raffle-commenters .comment-container:nth-child(1) {
    border-top: none;
}

.raffle-commenters .linked {
    border-left-color: #16a085;
}

.raffle-commenters .currUser {
    border-left-color: #34495e;
}

.comment-content {
    padding: 0 5px 0 55px;
    line-height: 20px;
}

/* CK-Editor */

.cke_contents {
    resize: vertical;
    padding-bottom: 12px !important;
}

.cke_editable{
    background: #f7f9fa!important;
    color: #2a2826 !important;
}

.cke_editable a {
    color: #0782C1 !important;
}
  
/* Global */

:root {
    --hover: brightness(1.2) !important;
}

body {
    color: var(--text) !important;
}

* {
    font-family: var(--globalFont);
}

h1, h1 *, h2, h2 *, h3, h3 * {
    font-family: var(--titleFont) !important;
}

.fa {
    font-family: FontAwesome !important;
}

a:active:not(.username), a:focus:not(.username), a:hover:not(.username), a:not(.username) {
    color: var(--link) !important;
}

span a:not(.username) {
    color: inherit !important;
}

a:active, a:focus, a:hover {
    -webkit-filter: var(--hover) !important;
    filter: var(--hover) !important;
}


/* Buttons input & labels */

.form-inline .form-control {
    color: var(--text) !important;
    background-color: var(--back1) !important;
}

.btn, .btn a, .rev-banking-background a, a.btn, a.btn:active, a.btn:focus, a.btn:hover, b.label {
    color: var(--btntext) !important;
    border-radius: var(--radius) !important;
}

.btn-success, .nav-pills>li>a {
    color: var(--btntext) !important;
    background-color: var(--btn4) !important;
}

.btn:active, .btn:focus, .btn:hover {
    -webkit-filter: var(--hover) !important;
    filter: var(--hover) !important;
    transition: 0.4s ease-out;
}

.nav-pills>li>a:focus, .nav-pills>li>a:hover, .nav-pills>li.active>a, .nav-pills>li.active>a:hover, .nav-pills>li.open>a, .nav-pills>li.open>a:hover {
    background-color: var(--btn4) !important;
    -webkit-filter: var(--hover) !important;
    filter: var(--hover) !important;
}

.btn.btn-danger, .btn.btn-danger.active, .btn.btn-danger:active, .open>.dropdown-toggle.btn.btn-danger, .progress-bar-danger {
    background-color: var(--btn1) !important;
    border-color: var(--btn1) !important;
}

.btn.btn-default, .btn.btn-default.active, .btn.btn-default:active, .btn.btn-default:hover, .btn.btn-info, .btn.btn-info.active, .btn.btn-info:active, .open>.dropdown-toggle.btn.btn-default, .open>.dropdown-toggle.btn.btn-info, .progress-bar-info {
    background-color: var(--btn2) !important;
    border-color: var(--btn2) !important;
}

.btn.btn-inverse, .btn.btn-inverse.active, .btn.btn-inverse:active, .open>.dropdown-toggle.btn.btn-inverse {
    background-color: var(--btn3) !important;
    border-color: var(--btn3) !important;
}

.btn-primary.active, .btn-primary:active, .btn.btn-primary, .open>.dropdown-toggle.btn-primary {
    background-color: var(--btn4) !important;
    border-color: var(--btn4) !important;
}

#GenKey pre {
    background-color: var(--back2) !important;
    border-color: var(--border) !important;
}


/* Select input */

.select-default .select2-choice, .select-default .select2-choice.hover, .select-default .select2-choice:active, .select-default .select2-choice:focus, .select-default .select2-choice:hover, .select2-drop .select2-result-selectable .select2-result-label:active, .select2-drop .select2-result-selectable .select2-result-label:focus, .select2-drop .select2-result-selectable .select2-result-label:hover {
    background-color: var(--btn4) !important;
    color: var(--btntext) !important;
}

.dropdown-menu>li>a, .select2-drop .select2-result-selectable .select2-result-label, .yamm-content li>a {
    color: var(--text) !important;
}

.select-primary .select2-choice, .select-primary .select2-choice.hover, .select-primary .select2-choice:active, .select-primary .select2-choice:focus, .select-primary .select2-choice:hover, .select2-drop .select2-highlighted>.select2-result-label {
    background-color: var(--btn4) !important;
    color: var(--btntext) !important;
}

.select2-chosen {
    color: var(--btntext) !important;
}

nav .iconic:before {
    color: var(--text) !important;
    opacity: 0.2;
}

.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
    background-color: var(--btn4) !important;
    color: var(--btntext) !important;
}


/* Search bar */

.popover {
    background-color: var(--back2) !important;
    color: var(--text) !important;
}

.popover li.active {
    background-color: var(--btn4) !important;
    color: var(--btntext) !important;
}


/* Checkbox & switch btn */

.checkbox input[type="checkbox"].custom-checkbox:checked+.icons .icon-checked, .checkbox input[type="radio"].custom-radio:checked+.icons .icon-checked, .radio input[type="checkbox"].custom-checkbox:checked+.icons .icon-checked, .radio input[type="radio"].custom-radio:checked+.icons .icon-checked {
    background-color: var(--btn4) !important;
}

.inv-switcher a, .item-group-switcher a, .search-switcher a {
    background-color: var(--btn4) !important;
    color: var(--btntext) !important;
}

.inv-switcher .active, .search-switcher .active, .toggle-chat-button {
    background-color: var(--btn2) !important;
    color: var(--btntext) !important;
}

.nav-notice a, .nav-notice a:active, .nav-notice a:focus, .nav-notice a:hover {
    color: var(--btntext) !important;
}


/* comments mention & page navigation */

a.user-mention, a.user-mention:focus, a.user-mention:hover, a.user-mention:visited {
    background-color: var(--btn4) !important;
    border-color: var(--btn4) !important;
    color: var(--btntext) !important;
}

.raffle-commenters .currUser {
    border-left-color: var(--btn2) !important;
}

.raffle-commenters .linked {
    border-left-color: var(--btn4) !important;
}

.alert-success {
    background-color: var(--btn4) !important;
    border-color: transparent !important;
    color: var(--btntext) !important;
}


/* Show up raffle owner */

.comment-container.owner {
    background-color: var(--back2) !important;
}

.dataTables_paginate>ul, .pagination>ul {
    background: var(--back2) !important;
}

.dataTables_paginate li.active>a, .dataTables_paginate li.active>span, .pagination li.active>a, .pagination li.active>span {
    background-color: var(--btn4) !important;
    border-color: var(--btn4) !important;
}

.dataTables_paginate li>a, .dataTables_paginate li>span, .pagination li>a, .pagination li>span {
    border-color: var(--border) !important;
}

.dataTables_paginate li>a:focus, .dataTables_paginate li>a:hover, .dataTables_paginate li>span:focus, .dataTables_paginate li>span:hover, .pagination li>a:focus, .pagination li>a:hover, .pagination li>span:focus, .pagination li>span:hover {
    background-color: var(--btn4) !important;
    border-color: var(--btn4) !important;
}


/* Infos box */

.tooltip .tooltip-inner {
    background-color: var(--btn3) !important;
}

.tooltip.top .tooltip-arrow {
    border-top-color: var(--btn3) !important;
}

.tooltip.bottom .tooltip-arrow {
    border-bottom-color: var(--btn3) !important;
    background-color: transparent !important;
}

.tooltip.left .tooltip-arrow {
    border-left-color: var(--btn3) !important;
}

.tooltip.right .tooltip-arrow {
    border-right-color: var(--btn3) !important;
}

.bg-info {
    background-color: transparent !important;
}


/* Main page & navbar */

.welcome {
    overflow: hidden;
    position: relative;
}

.welcome::before {
    content: "";
    background: var(--back1) !important;
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    opacity: 0.45;
}

.welcome-button {
    border: 2px solid var(--text) !important;
    color: var(--text) !important;
}

.welcome-status .tooltip {
    display: none !important;
}

.bank-selector-box a:focus, .bank-selector-box a:hover, .bank-selector-box a:hover, a[rel="tooltip"], a[rel="tooltip"]:hover {
    filter: none !important;
    -webkit-filter: none !important;
}

.bank-selector-box a:active, .bank-selector-box a:focus, .bank-selector-box a:hover {
    filter: none;
    -webkit-filter: none;
}

.welcome, .welcome-status a {
    color: var(--text) !important;
}

.welcome-status a:focus, .welcome-status a:hover {
    color: var(--btn4) !important;
}

.flat-slate, .navbar-default {
    background-color: var(--back1) !important;
}

.navbar-collapse ul.dropdown-menu, .select2-drop {
    background-color: var(--back2) !important;
}

.navbar .nav ul a:hover, ul.navbar-nav>li>a:not(.nav-userdropdown):focus, ul.navbar-nav>li>a:not(.nav-userdropdown):hover {
    color: var(--btntext) !important;
    background-color: var(--btn4) !important;
}

.navbar-default .navbar-nav>li>a {
    color: var(--text) !important;
}

.user-notices-unread {
    background-color: var(--btn1) !important;
    color: var(--btntext) !important;
}


/* Logo */

.svg {
    width: 171px;
    padding: 8px 12px;
    display: block;
    transition: filter, -webkit-filter 0.4s ease-out;
}

.svg:hover {
    -webkit-filter: var(--hover) !important;
    filter: var(--hover) !important;
}

.small-logo {
    display: none !important;
}

@media (max-width: 991px) {
    nav>div.container {
        width: 100% !important;
    }
}

@media (max-width: 910px) {
    .svg {
        width: 140px;
        padding: 10px 2px 0;
    }
    #navbar-collapse-01>ul:nth-child(1)>li:nth-child(6) {
        display: none;
    }
}

@media (min-width: 768px) and (max-width: 1200px) {
    .navbar .container {
        padding-left: 0 !important;
        padding-right: 0 !important;
    }
}


/* Raffle page, ( panel, panel-heading etc) */

#raffles-list .panel-left {
    background-color: #2A2826;
    border-right: 1px solid var(--border) !important;
}

.breadcrumb li, .breadcrumb li::after {
    color: var(--text) !important;
}

.form-control, .panel, .panel-info>.panel-heading, .well {
    color: var(--text) !important;
    background-color: var(--back1) !important;
    border-color: var(--border) !important;
}

.panel-info>.panel-heading, .panel-raffle, .panel-raffle .panel-heading, .raffle-mod {
    color: var(--btn2) !important;
    background-color: var(--back2) !important;
    border-color: var(--border) !important;
}

.raffle-hints {
    color: var(--text) !important;
    background-color: var(--back2) !important;
    border-color: var(--border) !important;
}

.panel-heading h3 {
    color: var(--btn2) !important;
}

.breadcrumb, .raffle-box-row, .raffle-info-col, .raffle-opts-row {
    color: var(--text) !important;
    background-color: var(--back2) !important;
    border-color: var(--border) !important;
}

.raffle-message:not(.raffle-entered-msg) {
    opacity: 0;
    transition: opacity 0.4s ease-out;
}

.raffle-message:not(.puzzle) {
    color: var(--text) !important;
    background-color: var(--back2) !important;
    border-color: var(--border) !important;
}

.raffle-password {
    opacity: 1 !important;
}

.raffle-message.puzzle {
    color: #2A2826 !important;
    background-color: #F7F9FA !important;
    border-color: var(--border) !important;
}

.raffle-entered-msg {
    background-color: var(--enterMsgColor) !important;
}

.input-group-btn .btn, .raffle-box-row, .raffle-commenters, .raffle-index-list, .raffle-info-col, .raffle-opts-row .col-xs-7 {
    border-color: var(--border) !important;
}

.raffle-commenters .comment-container {
    border-top-color: var(--border) !important;
}


/* Auctions */

.bg-success {
    background-color: var(--back1) !important;
    color: var(--btn4) !important;
}

.bg-warning {
    background-color: var(--back1) !important;
    color: var(--btn2) !important;
}

.text-danger {
    color: var(--btn1)!important;
}

.text-info {
    color: var(--btn2)!important;
}

.auction-warning {
    background-color: var(--btn1);
    color: var(--btntext);
}

.panel-auction {
    background-color: var(--back1);
    border-color: var(--border) !important;
}

.panel-heading {
    background-color: var(--back2) !important;
    border-color: var(--border) !important;
}


/* Profile */

.profile-about {
    border-color: var(--border) !important;
    color: #d0d0d0;
}

.profile-stat, .profile-stat:not(:last-child) {
    border-color: var(--border) !important;
    color: #919191;
}


/* Debug :hover tooltip with filter */

.profile-badges {
    position: relative;
    z-index: 1;
}


/* help */

.help-bg, .tab-content {
    background-color: var(--back1) !important;
    border-color: var(--border) !important;
}

.label-info, .label-info[href] {
    background-color: var(--btn2) !important;
    color: var(--btntext) !important;
}


/* Avatars */

.avatar-container img[alt="Avatar"], .comment-avatar img[alt="Avatar"] {
    border-radius: var(--avatarBorders) !important;
}

.avatar-hat {
    background-color: transparent !important;
}


/* Modal */

.modal-content, .modal-header {
    background-color: var(--back1) !important;
    border-color: var(--border) !important;
}

.modal-footer {
    background-color: var(--back2) !important;
}

.modal-header .close {
    color: var(--text) !important;
}

.modal-backdrop {
    background-color: var(--back1) !important;
    opacity: 0.85 !important;
}

.fullpage-well {
    background: var(--back1) !important;
}

#Content, .flat-dialog-inside {
    background: var(--back2) !important;
    color: var(--text) !important;
    border-color: var(--border) !important;
}


/* Table */

.table>tbody>tr>td, .table th, .table-bordered, .table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
    border-color: var(--border) !important;
}

.table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>th {
    color: var(--btn3);
}

.tooltip-arrow {
    background-color: transparent !important;
}

[rel="tooltip"] div {
    color: var(--btntext) !important;
    background-color: var(--btn4) !important;
}

td div div div:first-child {
    background-color: var(--btn1) !important;
}

td div div div:last-child {
    background-color: var(--btn4) !important;
}

.table-striped>tbody>tr:nth-child(odd), .table-striped>tbody>tr.selected-row {
    background-color: var(--back2) !important;
}

div[style="width:100%;background:#2ecc71"] {
    background-color: var(--btn4) !important;
}


/* Help center & About */

.jumbotron {
    background-color: transparent !important;
}

.panel-danger>.panel-heading, .panel-success>.panel-heading {
    background-color: var(--back2) !important;
    border-color: var(--border) !important;
}

.panel-success>.panel-heading {
    color: var(--btn4) !important;
}

.panel-danger>.panel-heading {
    color: var(--btn1) !important;
}

.panel-info>.panel-heading+.panel-collapse>.panel-body {
    border-color: var(--border) !important;
}

.user-helper {
    background: var(--back2) !important;
}

.about-inside * {
    color: #ddd !important;
}


/* Announcement */

.flat-block {
    background-color: var(--back2) !important;
    border: 1px solid var(--border) !important;
}

.notices-container .notice {
    color: var(--text) !important;
}

.notices-container .alert-danger {
    background-color: var(--back1) !important;
}

.notices-container .alert-danger:after {
    border-top-color: var(--back1) !important;
}

.notices-container .alert-warning {
    background-color: var(--back2) !important;
}

.notices-container .alert-warning:after {
    border-top-color: var(--back2) !important;
}

.notices-container .alert-info {
    background-color: var(--back2) !important;
}

.notices-container .alert-info:after {
    border-top-color: var(--back2) !important;
}

.notices-container .alert-success {
    background-color: var(--back2) !important;
}

.notices-container .alert-success:after {
    border-top-color: var(--back2) !important;
}


/* Raffle won */

.nav-notice {
    background-color: var(--btn1) !important;
    color: var(--btntext) !important;
}


/* Rules hidden link */

a.rules-accept-real {
    color: var(--text) !important;
}


/* Ban appeal */

.PostContainer {
    background-color: var(--back1) !important;
}

.bg-primary {
    background-color: var(--back2) !important;
    color: var(--text) !important;
}

.report-log {
    border-color: var(--border) !important;
}

.report-user {
    background-color: var(--back2) !important;
}

#report-text {
    resize: vertical!important;
    min-height: 100px;
}

/* FOOTER */
/*[[rafflecontent]]*/
/*[[custCSS]]*/
}
