css:
@-moz-document domain("techspot.com") {

aside.right {
    display: none !important;
}
#sidebar,
#billboard_placeholder {
    display: none !important;
}
div.sidebarContent {
    display: none !important;
}
body.home #content .wrapper .col-2-3 {
    width: 100%;
}
#feature {
    color: #ff0000;
}
.footer {
    display: none !important;
}
.related-products {
    display: none !important;
}
.related-query {
    display: none !important;
}
.related-products.related-stories {
    display: none !important;
}
.social {
    display: none !important;
}
.articleBody {
    width: 920px;
    text-align: justify;
    text-justify: inter-word;
    color: #eeeeee;
    background-color: #111111;
}
.intro-image {
    display: block;
    margin-left: auto;
    margin-right: auto;
}
.clearfix {
    background-color: #111111;
}
/* white text */

html,
body,
tbody,
thead,
th,
tr,
td,
blockquote,
li,
ul,
h1,
h2,
h3,
h4,
h5,
h6,
font,
strong,
p,
form,
footer,
fieldset,
.intro,
.h2 {
    color: #7e7171 !important;
}
a {
    color: #eeee00 !important;
}
#content {
    background-color: #171616;
}

#content .wrapper {
    background-color: #1e2933;
}

#ts_content {
    background-color: #1e2933;
}

#comments {
    background: #1e2933;
}

#comments #add-new-comment {
    background-color: #1e2933;
}

body.interior.news #content-header, body.interior.product #content-header, body.interior.download #content-header {
    background-color: #1e2933;
}

#comments .recently-commented-stories {
    background-color: #1e2933;
}

footer .copyright {
    background: #1e2933;
}

#header-ts .withHeader {
    background-color: #175093 !important;
}

h3.section-header {
    background: #1e1c1c;
}

.post-load-more {
    background-color: #1e1c1c;
}

}
