css:
@-moz-document url-prefix("https://www.netflix.com/watch/") {
#netflix-player .player-controls-wrapper { 
  bottom: 1px!important; }
#netflix-player .player-control-bar { 
  height: 6em!important; 
}
div#scrubber-component { 
  padding: 0!important; 
  margin-top: 3.05em!important; 
}
div.player-scrubber.horizontal .player-scrubber-target { 
  top: -2.1em; 
 
}
div.player-scrubber-handle {
  background: radial-gradient(/*[[pbh]]*/,/*[[pbh]]*/)!important;
}
div.player-scrubber-progress-completed {
  background: radial-gradient(/*[[pb]]*/,/*[[pb]]*/)!important;
}
section.player-slider > label { 
  margin-top: 0.5em; 
}
#netflix-player .player-controls-wrapper { 
  transition: transform 500ms ease-in-out; 
}
#netflix-player .player-controls-wrapper.opacity-transparent { 
  transform: translateY(8.6em);
  opacity: 1!important; 
}

.player-timedtext-text-container { 
  bottom: 10%!important; 
}
.player-timedtext-text-container span { 
    font-family: Arial,Helvetica,sans-serif,sans!important;
    font-size: 46px!important;
    -webkit-text-stroke-width: 1px;
    -webkit-text-stroke-color: #333;
    text-shadow: 2px 2px 8px #000!important;
}
}
