css:
@-moz-document domain("youtube.com") {


#guide-container {
    background: none !important;
    border:0px !important;
}

#guide.yt-scrollbar > div#appbar-guide-menu{
  background: url('https://s20.postimg.cc/9j29fcg4t/leftsidebar.jpg') -30px top fixed !important;
  background-size: 264px !important;
  background-repeat: no-repeat !important;
  border:0px !important;  
  }

#guide.yt-scrollbar{
  border:0px !important;  
  }

#guide{
  border:0px !important;  
  }

.guide-pinned.show-guide .guide-pinning-enabled #page, .guide-pinned.show-guide .guide-pinning-enabled #appbar-content {
    padding-left: 200px !important;
    background: #fff !important;
}  
  
#yt-masthead-container{
  background: url('https://s20.postimg.cc/vjilvyyst/topbgr.jpg') -30px top fixed !important;
  background-size: 122px !important;
  background-repeat: repeat-x !important;    
  border:0px !important;
}  
  
#masthead-appbar {
    border: 0px !important;
}  
  
  #appbar-guide-menu{
    border: 0px !important;    
  }
  
#appbar-nav{
   background: url('https://s20.postimg.cc/pjzg623e5/hanging.png') -10px -1px fixed !important;
   background-size: 200px !important;
   background-repeat: repeat-x !important;    
   border:0px !important;
  }
  
#appbar-nav.appbar-content-hidable > ul.appbar-nav-menu{
  padding-left: 15px !important;
  padding-right: 15px !important;
  background: rgba(255,255,255,0.9) !important;
  
  }  
  
.guide-pinned.show-guide .guide-pinning-enabled #page{
  background: #fff url('https://s20.postimg.cc/6sxivw8tp/Ba5_Jyb_L.jpg') right top fixed !important;
  background-size: cover !important;
  background-repeat: no-repeat !important;
  border:0px !important;  
  }
  
#browse-items-primary .item-section > li > .yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {
  background: transparent !important;

  }

.individual-feed{
    position: relative;
    background: transparent !important;
}  

  
.yt-card {
    margin: 0 0 10px;
    border: 0;
    background: rgba(255,255,255,0.5);
  }
  
  
  
  
  
  
  
}
