css:
@-moz-document domain("youtube.com") {
/*		/* YouTube Flat Dark & Clean v.0.7.2.1 */
/*               /* by MarUlberg */
/* Inspired by kirnbauer's "Youtube Dark Style Material [BETA]" */

/* Rounded Thumbnails */
img, #dismissed{
    border-radius: 10px !important;
}
ytd-thumbnail-overlay-time-status-renderer, ytd-thumbnail-overlay-resume-playback-renderer{
    opacity: 0 !important;
    transition: 200ms !important;
}
ytd-thumbnail:hover ytd-thumbnail-overlay-time-status-renderer{
    opacity: 1 !important;
    transition: 200ms !important;
}
yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer{
    opacity: 0.75 !important;
    transition: 200ms !important;
}
yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer{
    margin: 0px !important;
    width: 100% !important;
    color: transparent;
}
ytd-thumbnail-overlay-playback-status-renderer{
    height: 100% !important;
    width: 100% !important;
}
yt-icon > svg > g > g:nth-child(2), #country-code{
    display: none !important;
}
#search-form div, #search-icon-legacy{
    background: transparent !important;
    border: none !important;
}
#masthead-container #container, #guide-inner-content, yt-live-chat-header-renderer, #contents.yt-live-chat-item-list-renderer, #items.yt-live-chat-ticker-renderer, yt-live-chat-message-input-renderer, #show-hide-button.ytd-live-chat-frame > ytd-toggle-button-renderer.ytd-live-chat-frame, #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover, #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:focus, #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:active, ytd-mini-guide-renderer, ytd-mini-guide-entry-renderer{
    background: /*[[header]]*/ !important;
}
ytd-browse, yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer, ytd-page-manager > *.ytd-page-manager, .yt-live-chat-item-list-renderer, #content-pages.yt-live-chat-renderer, #columns.ytd-watch-flexy, yt-live-chat-ticker-renderer, ytd-guide-entry-renderer[active]{
    background: /*[[background]]*/ !important;
}
#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child), app-drawer.ytd-app:not([persistent]) #header.ytd-app, #search-form >div{
    border: none !important;
    box-shadow: none !important;
}
#scrim.visible.app-drawer {
    opacity: 0 !important;
}
yt-live-chat-message-input-renderer{
    border-radius: 20px 0px 0px 0px !important;
}
#show-hide-button.ytd-live-chat-frame > ytd-toggle-button-renderer.ytd-live-chat-frame{
    border-radius: 0px 0px 20px 6px !important;
}
yt-live-chat-header-renderer.yt-live-chat-renderer{
    border-radius: 6px 20px 0px 0px !important;
}
#items.yt-live-chat-ticker-renderer{
    border-radius: 0px 0px 6px 20px !important;
}
/*	Scroll Bar        	*/
*::-webkit-scrollbar{
    width: 14px !important;
}
*::-webkit-scrollbar-thumb{
    min-height: 35px !important;
    background: /*[[header]]*/ !important;
    border: none !important;
    border-radius: 0px !important;
}
*::-webkit-scrollbar-track-piece{
    background-color: /*[[background]]*/ !important;
    border: none !important;
}
}
