css:
@-moz-document domain("todoist.com") {
body {
    background: /*[[bgcl]]*/ !important;
}
div#top_bar_inner {
    margin: 0 auto !important;
    display: block;
    width: 100%;
    max-width: 1200px;
}
#logo {
    margin-left: 10px !important;
}
div#all_holder {
    max-width: 1200px !important;
    width: 100% !important;
    margin: 0 auto !important;
}
div#content {
    margin-left: 420px !important;
    width: 720px !important;
}
div#editor {
    /*width: calc(100vw - 375px) !important;*/
    
    width: 700px !important;
}
div#list_holder {
    width: 100% !important;
}
.arrow {
    margin-left: -15px !important
}
#left_menu {
    width: 400px !important;
}
.items li table td {
    padding-top: 7px !important;
    padding-bottom: 7px !important;
    vertical-align: middle !important;
}
#editor .arrow {
    margin-top: 10px !important;
    padding-left: 0 !important;
}
#quick_find {
    padding-top: 8px;
    display:inline-block !important;
    margin-left:25px !important;
}
#quick_find div {
    white-space: nowrap;
    padding-top: 0px !important;
    background: white;
    width: 375px;
}
#quick_find div .quick_find.fixed_pos {
    width: 100%;
}
.theme_noir .quick_find:hover {
    background: transparent !important;
}


#top_icons{
margin-right:20px !important;}


#top_bar{
    background-color: /*[[headercl]]*/ !important;
    border-color: /*[[headercl]]*/ !important;	
}
#s_completed_count .count{
left:5px !important;
}

}
