css:
@-moz-document domain("sousetsuka.com") {
/*
* Author: Ew0345
* Description: Dark Theme for Sousetsuka's Site.
*/
body {
    background: #232323 !important;
}
div.menucodenirvana {
    background: #20163A;
    /* Box Shadows Unchanged */
}
.nav li:hover ul {
    background-color: #20163A
    !important;
}
ul.nav li.arrow.hover a {
    color: #eee !important;
}
div.content-wrapper {
    /* Box Shadows Unchanged */
}
div.outerpic-wrapper {
    background-color: #232323 !important;
    color: #eee;
}
div.outerpic-wrapper a {
    color: #eee !important;
}
div.sidebar h2 {
    background: #20163A;
}
div.post.hentry {
    background-color: #232323 !important;
    color: #eee !important;
}
h1.post-title.entry-title a {
    color: #eee;
}
div.postmeta span.postLabels a {
    color: #eee;
}
div.post-body.entry-content {
    color: #eee !important;
}
.dsq-widget-comment p {
    color: #aaa !important;
}
span[dir="ltr"].post-count {
    color: #aaa !important;
}
.list-label-widget-content li {
    background-color: #303030 !important;
    border-width: 1px 1px 1px 1px !important;
    border-style: solid !important;
    border-color: #aaa !important;
}
.list-label-widget-content a[dir="ltr"] {
    color: #aaa !important;
}
.post-count {
    color: #aaa !important;
}
.gsc-input input {
    background-color: #303030 !important;
    border-color: #171717 !important;
    color: #eee !important;
}
input.gsc-search-button {
    background-color: #232323 !important;
    color: #ccc !important;
    border-style: solid;
    border-width: 1px 1px 1px 1px !important;
    border-color: #171717
}
span[style="color: white;"] span[style="font-size: x-small;"],
span[style="color: white;"] span[style="font-size: xx-small;"],
span[style="color: #f3f3f3;"] span[style="font-size: x-small;"],
span[style="color: #f3f3f3;"] span[style="font-size: xx-small;"] {
    color: #232323 !important;
}
}
