css:
@-moz-document domain(/*[[instance]]*/), domain(mstdn.jp), domain(pawoo.net) {
.drawer__inner,
.ui {
    background-color: /*[[uic]]*/;
}
.scrollable ,
.drawer__inner.darker,
.search-results__section *,
.tabs-bar__link:hover,
.drawer__header>a:hover,
.drawer__header>a:hover *,
.empty-column-indicator,
.suggestion-tags__body>ul>li,
.suggestion-tags__body>ul>li *{
    /*[[theme]]*/
}


  
  

.account__header div *:not(.account__header__avatar),
.account__header span,
.account__header__content,
.account__header .fa,
.muted a.status__content__spoiler-link,
.status__content__spoiler-link:hover >span,
.drawer__tab > .fa,
.drawer__tab:hover > .fa,
.drawer__block{
    background: rgba(0,0,0,0) !important;
}
.account__header>div {
    background: rgba(245, 245, 255, 0.7)!important;
}
.account__header *{
    color:#222 !important;
}
.status__content a,
.status__content a>span,
span.ellipsis,
.active,
.search__icon * {
    color: /*[[accent]]*/ !important;
}
.compose-form__buttons .active{
    background-color: #282c37;
}
.button{
    background-color: /*[[accent]]*/;
    opacity: 1;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.button:hover{
    background-color: /*[[accent]]*/;
    opacity: 0.8;
}
.search *,
.compose-form__buttons .active {
    border-radius: 0.4em;
}
.search__input,
.search__input:focus,
.compose-form__buttons,
.autosuggest-textarea__textarea,
.compose-form__modifiers {
    background-color: #F5F5FF;
}
.column-header,
.column-header *,
.getting-started__wrapper,
.getting-started__wrapper *,
.column-icon,
.tabs-bar,
.drawer__header,
.drawer__header *,
.column-back-button,
.search-results__header,
.favourite-tags *,
.trend-tags__header,
.column-header__collapsible-inner{
    background-color: /*[[tab]]*/;
    color: #FFF;
}
.column-header__collapsible-inner *{
   color: #FFF;
}
.status__content__spoiler-link{
    border-bottom: 2px dashed /*[[accent]]*/ ;
}
.status__content__spoiler-link:hover{
    border-bottom-color: #707B97;
}
.status__content__spoiler-link *{
    color:  /*[[accent]]*/  !important;
}
.status__content__spoiler-link:hover >span {
    color: #fff !important;
}
*::-webkit-input-placeholder {
    color: #bbb;
    opacity: 1;
}
*::-moz-placeholder {
    color: #bbb;
    opacity: 1;
    }
*:-ms-input-placeholder {
    color: #bbb;
}
.column-subheading,
.column-link:hover,
.active.column-header__button,
.active.column-header__button:hover{
    background: rgba(0,0,0,0.3) ;
    color:#fff;
}
.column-link>i,
.column-header__button>i{
    background: transparent;
}
.tabs-bar__link{
    border-bottom: 6px solid #000;
}
.active.tabs-bar__link{
    border-bottom: 6px solid rgba(0,0,0,0.3);
}
.active.tabs-bar__link *{
    color:#000;
}
}
