css:
@-moz-document domain("/*[[instance]]*/") {

div.drawer button.button {
    text-indent: -99999px;
    line-height: 0;
}
  
div.drawer button.button::after {
    content: "/*[[button]]*/";
    text-indent: 0;
    line-height: initial;
    display: block;
    margin-top: -2.1rem;
}

}
